import React, { useState } from 'react';
import { useAuth } from './AuthContext';
import { useNavigate } from 'react-router-dom';

function AdminLogin() {
    const { login, error, logout } = useAuth();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [loginError, setLoginError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await login(username, password);
            navigate('/events'); // Redirect to events after successful login
        } catch (err) {
            setLoginError(error || 'An unexpected error occurred'); // Display error
        }
    };

    const handleLogout = () => {
        logout();
        navigate('/admin'); // Redirect to admin login after logout
    };

    return (
        <div>
            <h2>Admin Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>
                        Username:
                        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
                    </label>
                </div>
                <div>
                    <label>
                        Password:
                        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    </label>
                </div>
                <button type="submit">Login</button>
                {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
            </form>
            {/* Show logout button after successful login */}
            <button onClick={handleLogout}>Logout</button>
        </div>
    );
}

export default AdminLogin;
