import React, { useState } from 'react';
import { useAuth } from './AuthProvider'; // Make sure the path to AuthProvider is correct

const Events = () => {
    const { user, logout, events, addEvent } = useAuth();
    const [newEvent, setNewEvent] = useState({ title: '', address: '', date: '', image: '' }); // Event state
    const [error, setError] = useState(null); // Holds error state

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewEvent((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        
        // Basic validation
        if (!newEvent.title || !newEvent.address || !newEvent.date || !newEvent.image) {
            setError('All fields are required.');
            return;
        }

        // Call addEvent from context
        addEvent(newEvent); 
        setNewEvent({ title: '', address: '', date: '', image: '' }); // Reset form
        setError(null); // Clear error message
    };

    const handleLogout = () => {
        logout();
    };

    return (
        <div>
            <h2>Upcoming Events</h2>
            {user && user.role === 'admin' && (
                <>
                    <button onClick={handleLogout} style={{ marginBottom: '10px' }}>
                        Logout
                    </button>
                    <h3>Add New Event</h3>
                    {error && <p style={{ color: 'red' }}>{error}</p>} {/* Show error message */}
                    <form onSubmit={handleSubmit}>
                        <input 
                            type="text" 
                            name="title" 
                            placeholder="Event Title" 
                            value={newEvent.title} 
                            onChange={handleInputChange} 
                            required 
                        />
                        <input 
                            type="text" 
                            name="address" 
                            placeholder="Event Address" 
                            value={newEvent.address} 
                            onChange={handleInputChange} 
                            required 
                        />
                        <input 
                            type="date" 
                            name="date" 
                            value={newEvent.date} 
                            onChange={handleInputChange} 
                            required 
                        />
                        <input 
                            type="text" 
                            name="image" 
                            placeholder="Image URL" 
                            value={newEvent.image} 
                            onChange={handleInputChange} 
                            required 
                        />
                        <button type="submit">Add Event</button>
                    </form>
                </>
            )}
            <h3>Event List</h3>
            <ul>
                {events.length > 0 ? (
                    events.map((event, index) => (
                        <li key={index}>
                            <h4>{event.title}</h4>
                            <p>{event.address}</p>
                            <p>{new Date(event.date).toLocaleDateString()}</p> {/* Format date */}
                            {event.image && (
                                <img src={event.image} alt={event.title} style={{ width: '100px', height: 'auto' }} />
                            )}
                        </li>
                    ))
                ) : (
                    <p>No upcoming events found.</p> // Show message if no events
                )}
            </ul>
        </div>
    );
};

export default Events;
