import React, { useState } from 'react';

const AdminLogin = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [eventDetails, setEventDetails] = useState({ title: '', date: '', description: '', photo: '' });
    const [failedAttempts, setFailedAttempts] = useState(0);
    const [isResetPasswordVisible, setIsResetPasswordVisible] = useState(false);
    const [newPassword, setNewPassword] = useState('');

    const handleLogin = (event) => {
        event.preventDefault();
        if (username === 'admin' && password === 'password') {
            setIsLoggedIn(true);
            setFailedAttempts(0);
            alert('Login successful!');
        } else {
            setFailedAttempts((prev) => prev + 1);
            alert('Invalid username or password');
            if (failedAttempts >= 2) {
                setIsResetPasswordVisible(true);
            }
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setPassword('');
        setEventDetails({ title: '', date: '', description: '', photo: '' });
        alert('Logged out successfully!');
    };

    const handleEventChange = (e) => {
        const { name, value } = e.target;
        setEventDetails((prevDetails) => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handleEventSubmit = (e) => {
        e.preventDefault();
        // Replace this with actual logic to save event details
        console.log('Event Details:', eventDetails);
        alert('Event added successfully!');
        // Reset event details
        setEventDetails({ title: '', date: '', description: '', photo: '' });
    };

    const handlePasswordReset = (e) => {
        e.preventDefault();
        // Implement logic for resetting the password
        console.log('New Password:', newPassword);
        alert('Password has been reset successfully!');
        setIsResetPasswordVisible(false);
        setNewPassword('');
        // Optionally log the admin back in
        setIsLoggedIn(true);
        setUsername('admin'); // or prompt for username
        setPassword(newPassword); // new password for login
    };

    return (
        <div>
            <h2>Admin Login</h2>
            {isLoggedIn ? (
                <div>
                    <h3>Welcome, Admin!</h3>
                    <button onClick={handleLogout}>Logout</button>
                    {/* Event management form */}
                    <form onSubmit={handleEventSubmit}>
                        <h4>Add Event</h4>
                        <div>
                            <label htmlFor="title">Event Title:</label>
                            <input 
                                type="text" 
                                id="title" 
                                name="title" 
                                value={eventDetails.title} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="date">Event Date:</label>
                            <input 
                                type="date" 
                                id="date" 
                                name="date" 
                                value={eventDetails.date} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="description">Description:</label>
                            <textarea 
                                id="description" 
                                name="description" 
                                value={eventDetails.description} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="photo">Event Photo URL:</label>
                            <input 
                                type="url" 
                                id="photo" 
                                name="photo" 
                                value={eventDetails.photo} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <button type="submit">Add Event</button>
                    </form>
                </div>
            ) : (
                <form onSubmit={handleLogin}>
                    <div>
                        <label htmlFor="username">Username:</label>
                        <input 
                            type="text" 
                            id="username" 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)} 
                            required 
                        />
                    </div>
                    <div>
                        <label htmlFor="password">Password:</label>
                        <input 
                            type="password" 
                            id="password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>
            )}
            {isResetPasswordVisible && (
                <div>
                    <h4>Reset Password</h4>
                    <form onSubmit={handlePasswordReset}>
                        <div>
                            <label htmlFor="newPassword">New Password:</label>
                            <input 
                                type="password" 
                                id="newPassword" 
                                value={newPassword} 
                                onChange={(e) => setNewPassword(e.target.value)} 
                                required 
                            />
                        </div>
                        <button type="submit">Reset Password</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default AdminLogin;
