import React, { useState } from 'react';

const PhotosVideo = () => {
    const [media, setMedia] = useState([]);
    const [mediaFile, setMediaFile] = useState(null);
    const [mediaType, setMediaType] = useState('image'); // default type is image

    const handleFileChange = (e) => {
        setMediaFile(e.target.files[0]);
    };

    const handleTypeChange = (e) => {
        setMediaType(e.target.value);
    };

    const handleUpload = (e) => {
        e.preventDefault();
        if (mediaFile) {
            const newMedia = {
                file: URL.createObjectURL(mediaFile), // Create a URL for the uploaded file
                type: mediaType,
            };
            setMedia([...media, newMedia]);
            setMediaFile(null); // Reset the file input
        }
    };

    return (
        <div>
            <h2>Upload Photos and Videos</h2>
            <form onSubmit={handleUpload}>
                <div>
                    <label>Media Type:</label>
                    <select value={mediaType} onChange={handleTypeChange}>
                        <option value="image">Image</option>
                        <option value="video">Video</option>
                    </select>
                </div>
                <div>
                    <label>{mediaType === 'image' ? 'Upload Image' : 'Upload Video'}:</label>
                    <input
                        type="file"
                        accept={mediaType === 'image' ? 'image/*' : 'video/*'}
                        onChange={handleFileChange}
                        required
                    />
                </div>
                <button type="submit">Upload</button>
            </form>

            <h2>Uploaded Media</h2>
            <div className="media-list">
                {media.map((item, index) => (
                    <div key={index} className="media">
                        {item.type === 'image' ? (
                            <img src={item.file} alt={`Uploaded Media ${index}`} style={{ width: '300px', height: 'auto' }} />
                        ) : (
                            <video controls style={{ width: '300px', height: 'auto' }}>
                                <source src={item.file} type="video/mp4" />
                                Your browser does not support the video tag.
                            </video>
                        )}
                    </div>
                ))}
            </div>

            <style jsx>{`
                .media-list {
                    margin-top: 20px;
                }
                .media {
                    border: 1px solid #ccc;
                    padding: 10px;
                    margin: 10px 0;
                }
            `}</style>
        </div>
    );
};

export default PhotosVideo;
