import React, { useState } from 'react';
import './Donations.css';

const Donations = () => {
  const [donationAmount, setDonationAmount] = useState('');
  const [customAmount, setCustomAmount] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('card');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const predefinedAmounts = [10, 25, 50, 100, 250, 500];

  const handleAmountSelect = (amount) => {
    setDonationAmount(amount);
    setCustomAmount('');
  };

  const handleCustomAmountChange = (e) => {
    const value = e.target.value;
    setCustomAmount(value);
    setDonationAmount('custom');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const finalAmount = donationAmount === 'custom' ? customAmount : donationAmount;
    console.log({
      ...formData,
      amount: finalAmount,
      paymentMethod
    });
    // TODO: Implement payment processing
  };

  return (
    <div className="donations-page">
      <div className="donations-header">
        <h1 className="section-title">Support Our Cause</h1>
        <p className="section-description">
          Your donation helps us preserve and promote Edo cultural heritage in Italy.
          Every contribution makes a difference in our community.
        </p>
      </div>

      <div className="donations-container">
        <div className="donation-info">
          <h2>Why Donate?</h2>
          <div className="donation-benefits">
            <div className="benefit-card">
              <i className="fas fa-culture"></i>
              <h3>Cultural Preservation</h3>
              <p>Help preserve and promote Edo cultural heritage for future generations</p>
            </div>
            <div className="benefit-card">
              <i className="fas fa-hands-helping"></i>
              <h3>Community Support</h3>
              <p>Support community programs and cultural exchange initiatives</p>
            </div>
            <div className="benefit-card">
              <i className="fas fa-graduation-cap"></i>
              <h3>Education</h3>
              <p>Fund educational programs and cultural workshops</p>
            </div>
          </div>
        </div>

        <div className="donation-form">
          <h2>Make a Donation</h2>
          <form onSubmit={handleSubmit}>
            <div className="amount-selection">
              <h3>Select Amount (€)</h3>
              <div className="amount-buttons">
                {predefinedAmounts.map(amount => (
                  <button
                    key={amount}
                    type="button"
                    className={`amount-btn ${donationAmount === amount ? 'selected' : ''}`}
                    onClick={() => handleAmountSelect(amount)}
                  >
                    €{amount}
                  </button>
                ))}
                <div className="custom-amount">
                  <input
                    type="number"
                    placeholder="Custom amount"
                    value={customAmount}
                    onChange={handleCustomAmountChange}
                    onClick={() => setDonationAmount('custom')}
                  />
                </div>
              </div>
            </div>

            <div className="payment-method">
              <h3>Payment Method</h3>
              <div className="payment-options">
                <label>
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="card"
                    checked={paymentMethod === 'card'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                  />
                  Credit/Debit Card
                </label>
                <label>
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="paypal"
                    checked={paymentMethod === 'paypal'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                  />
                  PayPal
                </label>
                <label>
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="bank"
                    checked={paymentMethod === 'bank'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                  />
                  Bank Transfer
                </label>
              </div>
            </div>

            <div className="donor-info">
              <h3>Your Information</h3>
              <div className="form-group">
                <label htmlFor="name">Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="message">Message (Optional)</label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleInputChange}
                  placeholder="Leave a message with your donation"
                ></textarea>
              </div>
            </div>

            <button type="submit" className="donate-btn">
              Complete Donation
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Donations;
