import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';
import { useShared } from '../../context/SharedContext';

const GalleryManagement = () => {
  const { gallery, addGalleryItem, updateGalleryItem, deleteGalleryItem } = useShared();
  const [selectedFile, setSelectedFile] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedFile && !editingId) return;

    const item = {
      title,
      description,
      image: previewUrl || (editingId && gallery.find(item => item.id === editingId)?.image),
    };

    if (editingId) {
      updateGalleryItem(editingId, item);
      setEditingId(null);
    } else {
      addGalleryItem(item);
    }

    // Reset form
    setTitle('');
    setDescription('');
    setSelectedFile(null);
    setPreviewUrl(null);
  };

  const handleEdit = (item) => {
    setEditingId(item.id);
    setTitle(item.title);
    setDescription(item.description);
    setPreviewUrl(item.image);
  };

  const handleCancel = () => {
    setEditingId(null);
    setTitle('');
    setDescription('');
    setSelectedFile(null);
    setPreviewUrl(null);
  };

  return (
    <Container className="py-5">
      <h2 className="mb-4">{editingId ? 'Edit Gallery Item' : 'Add New Gallery Item'}</h2>
      <Row>
        <Col md={6}>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Image</Form.Label>
              <Form.Control
                type="file"
                onChange={handleFileChange}
                accept="image/*"
                required={!editingId}
              />
            </Form.Group>

            {previewUrl && (
              <div className="mb-3">
                <img
                  src={previewUrl}
                  alt="Preview"
                  style={{ maxWidth: '100%', height: 'auto' }}
                  className="rounded"
                />
              </div>
            )}

            <div className="d-flex gap-2">
              <Button type="submit" variant="primary">
                {editingId ? 'Update Item' : 'Add Item'}
              </Button>
              {editingId && (
                <Button variant="secondary" onClick={handleCancel}>
                  Cancel
                </Button>
              )}
            </div>
          </Form>
        </Col>
      </Row>

      <h3 className="mt-5 mb-4">Gallery Items</h3>
      <Row className="g-4">
        {gallery.map((item) => (
          <Col key={item.id} md={4}>
            <Card>
              <Card.Img
                variant="top"
                src={item.image}
                style={{ height: '200px', objectFit: 'cover' }}
              />
              <Card.Body>
                <Card.Title>{item.title}</Card.Title>
                <Card.Text>{item.description}</Card.Text>
                <div className="d-flex gap-2">
                  <Button
                    variant="outline-primary"
                    size="sm"
                    onClick={() => handleEdit(item)}
                  >
                    Edit
                  </Button>
                  <Button
                    variant="outline-danger"
                    size="sm"
                    onClick={() => deleteGalleryItem(item.id)}
                  >
                    Delete
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default GalleryManagement;
