import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Table, Modal } from 'react-bootstrap';
import { useMemberCount } from '../../context/MemberContext';

const MeetingsManagement = () => {
  const { applications } = useMemberCount();
  const [meetings, setMeetings] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedMeeting, setSelectedMeeting] = useState(null);
  const [newMeeting, setNewMeeting] = useState({
    title: '',
    date: '',
    time: '',
    location: '',
    description: '',
    attendees: []
  });

  useEffect(() => {
    // Load meetings from localStorage
    const storedMeetings = JSON.parse(localStorage.getItem('meetings') || '[]');
    setMeetings(storedMeetings);
  }, []);

  const approvedMembers = applications.filter(app => app.status === 'approved');

  const handleAddMeeting = (e) => {
    e.preventDefault();
    
    if (!newMeeting.title || !newMeeting.date || !newMeeting.time || !newMeeting.location) {
      alert('Please fill in all required fields');
      return;
    }

    const meeting = {
      id: Date.now(),
      ...newMeeting,
      createdAt: new Date().toISOString(),
      attendees: []
    };

    const updatedMeetings = [...meetings, meeting];
    setMeetings(updatedMeetings);
    localStorage.setItem('meetings', JSON.stringify(updatedMeetings));
    
    setNewMeeting({
      title: '',
      date: '',
      time: '',
      location: '',
      description: '',
      attendees: []
    });

    alert('Meeting scheduled successfully!');
  };

  const handleViewAttendance = (meeting) => {
    setSelectedMeeting(meeting);
    setShowModal(true);
  };

  const handleUpdateAttendance = (memberId) => {
    const updatedMeeting = {
      ...selectedMeeting,
      attendees: selectedMeeting.attendees.includes(memberId)
        ? selectedMeeting.attendees.filter(id => id !== memberId)
        : [...selectedMeeting.attendees, memberId]
    };

    const updatedMeetings = meetings.map(meeting =>
      meeting.id === selectedMeeting.id ? updatedMeeting : meeting
    );

    setMeetings(updatedMeetings);
    setSelectedMeeting(updatedMeeting);
    localStorage.setItem('meetings', JSON.stringify(updatedMeetings));
  };

  const handleDeleteMeeting = (meetingId) => {
    if (window.confirm('Are you sure you want to delete this meeting?')) {
      const updatedMeetings = meetings.filter(meeting => meeting.id !== meetingId);
      setMeetings(updatedMeetings);
      localStorage.setItem('meetings', JSON.stringify(updatedMeetings));
    }
  };

  return (
    <Container className="py-5">
      <div className="admin-section">
        <h2 className="admin-section-title">
          <i className="fas fa-clipboard-list"></i>
          Meetings & Attendance
        </h2>

        <Row>
          <Col md={4}>
            <div className="admin-card">
              <h3 className="management-title">Schedule New Meeting</h3>
              <Form onSubmit={handleAddMeeting}>
                <Form.Group className="mb-3">
                  <Form.Label>Meeting Title*</Form.Label>
                  <Form.Control
                    type="text"
                    value={newMeeting.title}
                    onChange={(e) => setNewMeeting({...newMeeting, title: e.target.value})}
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Date*</Form.Label>
                  <Form.Control
                    type="date"
                    value={newMeeting.date}
                    onChange={(e) => setNewMeeting({...newMeeting, date: e.target.value})}
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Time*</Form.Label>
                  <Form.Control
                    type="time"
                    value={newMeeting.time}
                    onChange={(e) => setNewMeeting({...newMeeting, time: e.target.value})}
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Location*</Form.Label>
                  <Form.Control
                    type="text"
                    value={newMeeting.location}
                    onChange={(e) => setNewMeeting({...newMeeting, location: e.target.value})}
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Description</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    value={newMeeting.description}
                    onChange={(e) => setNewMeeting({...newMeeting, description: e.target.value})}
                  />
                </Form.Group>

                <Button type="submit" variant="primary" className="w-100">
                  Schedule Meeting
                </Button>
              </Form>
            </div>
          </Col>

          <Col md={8}>
            <div className="admin-card">
              <h3 className="management-title">Scheduled Meetings</h3>
              <Table responsive striped hover>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Date</th>
                    <th>Time</th>
                    <th>Location</th>
                    <th>Attendance</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {meetings.map(meeting => (
                    <tr key={meeting.id}>
                      <td>{meeting.title}</td>
                      <td>{meeting.date}</td>
                      <td>{meeting.time}</td>
                      <td>{meeting.location}</td>
                      <td>
                        {meeting.attendees?.length || 0} / {approvedMembers.length}
                      </td>
                      <td>
                        <Button
                          variant="outline-primary"
                          size="sm"
                          className="me-2"
                          onClick={() => handleViewAttendance(meeting)}
                        >
                          Attendance
                        </Button>
                        <Button
                          variant="outline-danger"
                          size="sm"
                          onClick={() => handleDeleteMeeting(meeting.id)}
                        >
                          Delete
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>

              {meetings.length === 0 && (
                <div className="text-center py-5">
                  <p className="text-muted mb-0">No meetings scheduled</p>
                </div>
              )}
            </div>
          </Col>
        </Row>
      </div>

      {/* Attendance Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Meeting Attendance</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h5 className="mb-3">{selectedMeeting?.title}</h5>
          <p className="text-muted">
            {selectedMeeting?.date} at {selectedMeeting?.time}
          </p>
          
          <Table responsive striped hover>
            <thead>
              <tr>
                <th>Member Name</th>
                <th>Attendance</th>
              </tr>
            </thead>
            <tbody>
              {approvedMembers.map(member => (
                <tr key={member.id}>
                  <td>{member.surname} {member.name}</td>
                  <td>
                    <Form.Check
                      type="checkbox"
                      label="Present"
                      checked={selectedMeeting?.attendees.includes(member.id)}
                      onChange={() => handleUpdateAttendance(member.id)}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default MeetingsManagement;
