import React, { useState } from 'react';
import './Gallery.css';

const Gallery = () => {
  const [selectedImage, setSelectedImage] = useState(null);

  // Sample gallery data - replace with actual images
  const galleryItems = [
    {
      id: 1,
      title: 'Cultural Festival 2023',
      image: '/gallery/festival-2023.jpg',
      category: 'events'
    },
    {
      id: 2,
      title: 'Traditional Dance Performance',
      image: '/gallery/dance.jpg',
      category: 'culture'
    },
    {
      id: 3,
      title: 'Community Gathering',
      image: '/gallery/community.jpg',
      category: 'community'
    },
    // Add more gallery items here
  ];

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  const closeModal = () => {
    setSelectedImage(null);
  };

  return (
    <div className="gallery-page">
      <div className="gallery-header">
        <h1 className="section-title">Photo Gallery</h1>
        <p className="section-description">
          Explore moments from our events, cultural celebrations, and community gatherings
        </p>
      </div>

      <div className="gallery-filters">
        <button className="filter-btn active">All</button>
        <button className="filter-btn">Events</button>
        <button className="filter-btn">Culture</button>
        <button className="filter-btn">Community</button>
      </div>

      <div className="gallery-grid">
        {galleryItems.map((item) => (
          <div key={item.id} className="gallery-item">
            <img
              src={item.image}
              alt={item.title}
              onClick={() => handleImageClick(item)}
            />
            <div className="gallery-item-overlay">
              <h3>{item.title}</h3>
              <span className="category">{item.category}</span>
            </div>
          </div>
        ))}
      </div>

      {selectedImage && (
        <div className="modal" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="close-btn" onClick={closeModal}>Ã—</button>
            <img src={selectedImage.image} alt={selectedImage.title} />
            <div className="modal-info">
              <h3>{selectedImage.title}</h3>
              <span className="category">{selectedImage.category}</span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery;
