import React, { createContext, useState, useContext, useEffect } from 'react';

const SharedContext = createContext();

export const SharedProvider = ({ children }) => {
  const [events, setEvents] = useState([]);
  const [gallery, setGallery] = useState([]);

  // Load initial data from localStorage
  useEffect(() => {
    const storedEvents = JSON.parse(localStorage.getItem('events') || '[]');
    const storedGallery = JSON.parse(localStorage.getItem('gallery') || '[]');
    setEvents(storedEvents);
    setGallery(storedGallery);
  }, []);

  // Events management
  const addEvent = (event) => {
    const updatedEvents = [...events, event];
    setEvents(updatedEvents);
    localStorage.setItem('events', JSON.stringify(updatedEvents));
  };

  const updateEvent = (eventId, updatedEvent) => {
    const updatedEvents = events.map(event =>
      event.id === eventId ? { ...event, ...updatedEvent } : event
    );
    setEvents(updatedEvents);
    localStorage.setItem('events', JSON.stringify(updatedEvents));
  };

  const deleteEvent = (eventId) => {
    const updatedEvents = events.filter(event => event.id !== eventId);
    setEvents(updatedEvents);
    localStorage.setItem('events', JSON.stringify(updatedEvents));
  };

  // Gallery management
  const addImage = (image) => {
    const updatedGallery = [...gallery, image];
    setGallery(updatedGallery);
    localStorage.setItem('gallery', JSON.stringify(updatedGallery));
  };

  const updateImage = (imageId, updatedImage) => {
    const updatedGallery = gallery.map(image =>
      image.id === imageId ? { ...image, ...updatedImage } : image
    );
    setGallery(updatedGallery);
    localStorage.setItem('gallery', JSON.stringify(updatedGallery));
  };

  const deleteImage = (imageId) => {
    const updatedGallery = gallery.filter(image => image.id !== imageId);
    setGallery(updatedGallery);
    localStorage.setItem('gallery', JSON.stringify(updatedGallery));
  };

  return (
    <SharedContext.Provider
      value={{
        events,
        gallery,
        addEvent,
        updateEvent,
        deleteEvent,
        addImage,
        updateImage,
        deleteImage,
      }}
    >
      {children}
    </SharedContext.Provider>
  );
};

export const useShared = () => {
  const context = useContext(SharedContext);
  if (!context) {
    throw new Error('useShared must be used within a SharedProvider');
  }
  return context;
};
