import React, { useState } from 'react';
import './AdminPanel.css';

const EventManagement = () => {
  const [events, setEvents] = useState([
    {
      id: 1,
      title: 'Annual Cultural Festival',
      date: '2024-06-15',
      location: 'Udine City Center',
      description: 'Annual celebration of Edo culture'
    }
  ]);
  const [showForm, setShowForm] = useState(false);
  const [currentEvent, setCurrentEvent] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    date: '',
    location: '',
    description: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (currentEvent) {
      // Edit existing event
      setEvents(events.map(event =>
        event.id === currentEvent.id
          ? { ...formData, id: event.id }
          : event
      ));
    } else {
      // Add new event
      setEvents([...events, { ...formData, id: Date.now() }]);
    }
    handleCloseForm();
  };

  const handleEdit = (event) => {
    setCurrentEvent(event);
    setFormData(event);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      setEvents(events.filter(event => event.id !== id));
    }
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setCurrentEvent(null);
    setFormData({
      title: '',
      date: '',
      location: '',
      description: ''
    });
  };

  return (
    <div className="admin-section">
      <div className="section-header">
        <h2>Event Management</h2>
        <button className="add-btn" onClick={() => setShowForm(true)}>
          Add New Event
        </button>
      </div>

      {showForm && (
        <div className="modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{currentEvent ? 'Edit Event' : 'Add New Event'}</h3>
              <button className="close-btn" onClick={handleCloseForm}>&times;</button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="title">Title</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="date">Date</label>
                <input
                  type="date"
                  id="date"
                  name="date"
                  value={formData.date}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="location">Location</label>
                <input
                  type="text"
                  id="location"
                  name="location"
                  value={formData.location}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea
                  id="description"
                  name="description"
                  value={formData.description}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-actions">
                <button type="submit" className="submit-btn">
                  {currentEvent ? 'Update Event' : 'Add Event'}
                </button>
                <button type="button" className="cancel-btn" onClick={handleCloseForm}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Date</th>
              <th>Location</th>
              <th>Description</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {events.map(event => (
              <tr key={event.id}>
                <td>{event.title}</td>
                <td>{new Date(event.date).toLocaleDateString()}</td>
                <td>{event.location}</td>
                <td>{event.description}</td>
                <td>
                  <button className="edit-btn" onClick={() => handleEdit(event)}>
                    Edit
                  </button>
                  <button className="delete-btn" onClick={() => handleDelete(event.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EventManagement;
