import React, { useState } from 'react';
import { useAuth } from './AuthProvider';

const AdminLogin = () => {
    const { user, login, events, addEvent, removeEvent, members, addMember } = useAuth(); // Accessing members and addMember
    const [newEvent, setNewEvent] = useState({
        name: '',
        address: '',
        picture: '',
        location: '',
        signedBy: '',
        date: '',
        venue: ''
    });

    const [newMember, setNewMember] = useState({
        surname: '',
        name: '',
        dateOfBirth: '',
        townOfBirth: '',
        localGov: '',
        passport: '',
        codiceFiscale: '',
        phone: '',
        maritalStatus: '',
        parentalStatus: '',
        clubMembership: '',
        criminalRecords: '',
        reasonForEnrolling: '',
        note: '',
        vow: ''
    });

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);
    const [error, setError] = useState('');

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewEvent((prev) => ({ ...prev, [name]: value }));
    };

    const handleMemberChange = (e) => {
        const { name, value } = e.target;
        setNewMember((prev) => ({ ...prev, [name]: value })); // Update member fields
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        try {
            await login(username, password, rememberMe);
        } catch (err) {
            setError('Invalid username or password');
        }
    };

    const handleSubmitEvent = (e) => {
        e.preventDefault();
        addEvent(newEvent); // Use addEvent from context
        setNewEvent({ name: '', address: '', picture: '', location: '', signedBy: '', date: '', venue: '' }); // Reset form
    };

    const handleRemoveEvent = (event) => {
        removeEvent(event); // Use removeEvent from context
    };

    const handleSubmitMember = (e) => {
        e.preventDefault();
        addMember(newMember); // Add new member using addMember from context
        setNewMember({ // Reset member form
            surname: '',
            name: '',
            dateOfBirth: '',
            townOfBirth: '',
            localGov: '',
            passport: '',
            codiceFiscale: '',
            phone: '',
            maritalStatus: '',
            parentalStatus: '',
            clubMembership: '',
            criminalRecords: '',
            reasonForEnrolling: '',
            note: '',
            vow: ''
        });
    };

    return (
        <div>
            {!user ? (
                <div>
                    <h2>Admin Login</h2>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <form onSubmit={handleLogin}>
                        <input 
                            type="text" 
                            placeholder="Username" 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)} 
                            required 
                        />
                        <input 
                            type="password" 
                            placeholder="Password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                        <label>
                            <input 
                                type="checkbox" 
                                checked={rememberMe} 
                                onChange={(e) => setRememberMe(e.target.checked)} 
                            />
                            Remember Me
                        </label>
                        <button type="submit">Login</button>
                    </form>
                </div>
            ) : (
                <>
                    <h3>Add New Event</h3>
                    <form onSubmit={handleSubmitEvent}>
                        <input type="text" name="name" placeholder="Event Name" value={newEvent.name} onChange={handleInputChange} required />
                        <input type="text" name="address" placeholder="Event Address" value={newEvent.address} onChange={handleInputChange} required />
                        <input type="file" name="picture" placeholder="Event Picture" onChange={(e) => setNewEvent({ ...newEvent, picture: URL.createObjectURL(e.target.files[0]) })} required />
                        <input type="text" name="location" placeholder="Location" value={newEvent.location} onChange={handleInputChange} required />
                        <input type="text" name="signedBy" placeholder="Signed By Chairman" value={newEvent.signedBy} onChange={handleInputChange} required />
                        <input type="date" name="date" value={newEvent.date} onChange={handleInputChange} required />
                        <input type="text" name="venue" placeholder="Venue" value={newEvent.venue} onChange={handleInputChange} required />
                        <button type="submit">Add Event</button>
                    </form>

                    <h3>Event List</h3>
                    <ul>
                        {events.map((event, index) => (
                            <li key={index}>
                                <h4>{event.name}</h4>
                                <p>Address: {event.address}</p>
                                <p>Location: {event.location}</p>
                                <p>Date: {event.date}</p>
                                <p>Signed By: {event.signedBy}</p>
                                {event.picture && <img src={event.picture} alt={event.name} style={{ width: '100px', height: 'auto' }} />}
                                <button onClick={() => handleRemoveEvent(event)}>Remove Event</button>
                            </li>
                        ))}
                    </ul>

                    <h3>Add New Member</h3>
                    <form onSubmit={handleSubmitMember}>
                        <input type="text" name="surname" placeholder="Surname" value={newMember.surname} onChange={handleMemberChange} required />
                        <input type="text" name="name" placeholder="Name" value={newMember.name} onChange={handleMemberChange} required />
                        <input type="date" name="dateOfBirth" placeholder="Date of Birth" value={newMember.dateOfBirth} onChange={handleMemberChange} required />
                        <input type="text" name="townOfBirth" placeholder="Town/City of Birth" value={newMember.townOfBirth} onChange={handleMemberChange} required />
                        <input type="text" name="localGov" placeholder="Local Government of Origin" value={newMember.localGov} onChange={handleMemberChange} required />
                        <input type="text" name="passport" placeholder="Passport/ID Number" value={newMember.passport} onChange={handleMemberChange} required />
                        <input type="text" name="codiceFiscale" placeholder="Codice Fiscale" value={newMember.codiceFiscale} onChange={handleMemberChange} required />
                        <input type="text" name="phone" placeholder="Phone/WhatsApp Number" value={newMember.phone} onChange={handleMemberChange} required />
                        <input type="text" name="maritalStatus" placeholder="Marital Status" value={newMember.maritalStatus} onChange={handleMemberChange} required />
                        <input type="text" name="parentalStatus" placeholder="Parental Status" value={newMember.parentalStatus} onChange={handleMemberChange} required />
                        <input type="text" name="clubMembership" placeholder="Association/Club Membership Details" value={newMember.clubMembership} onChange={handleMemberChange} required />
                        <input type="text" name="criminalRecords" placeholder="Criminal Records" value={newMember.criminalRecords} onChange={handleMemberChange} required />
                        <input type="text" name="reasonForEnrolling" placeholder="Reason for Enrolling" value={newMember.reasonForEnrolling} onChange={handleMemberChange} required />
                        <textarea name="note" placeholder="Note" value={newMember.note} onChange={handleMemberChange} required></textarea>
                        <textarea name="vow" placeholder="Vow" value={newMember.vow} onChange={handleMemberChange} required></textarea>
                        <button type="submit">Add Member</button>
                    </form>

                    <h3>Registered Members</h3>
                    <ul>
                        {members.map((member, index) => (
                            <li key={index}>
                                <h4>{member.name} {member.surname}</h4>
                                <p>Date of Birth: {member.dateOfBirth}</p>
                                <p>Town/City of Birth: {member.townOfBirth}</p>
                                <p>Local Government: {member.localGov}</p>
                                <p>Phone: {member.phone}</p>
                                {/* You can add more member details here */}
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default AdminLogin;
