import React, { useState } from 'react';
import { useAuth } from './AuthProvider';

const AdminLogin = () => {
    const { user, addMember, members } = useAuth(); // Remove unused variables
    const [newMember, setNewMember] = useState({ /* member fields */ });
    const [error, setError] = useState('');

    const handleMemberChange = (e) => {
        const { name, value } = e.target;
        setNewMember((prev) => ({ ...prev, [name]: value }));
    };

    const handleMemberSubmit = (e) => {
        e.preventDefault();
        addMember(newMember); // Call to context to add member
        setNewMember({ /* reset member state */ });
    };

    return (
        <div>
            {!user ? (
                <div>
                    <h2>Admin Login</h2>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <form onSubmit={handleLogin}>
                        {/* Add login form if needed */}
                    </form>
                </div>
            ) : (
                <>
                    <h3>Add New Member</h3>
                    <form onSubmit={handleMemberSubmit}>
                        <input type="text" name="surname" placeholder="Surname" value={newMember.surname} onChange={handleMemberChange} required />
                        {/* other input fields */}
                        <button type="submit">Add Member</button>
                    </form>

                    <h3>Registered Members</h3>
                    <ul>
                        {members.map((member, index) => (
                            <li key={index}>
                                <h4>{member.name} {member.surname}</h4>
                                {/* other member details */}
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default AdminLogin;
