import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Adjust the import based on your actual AuthContext path

const ProtectedRoute = ({ children }) => {
    const { isAdmin } = useAuth(); // Assuming your AuthContext provides a way to check if the user is an admin

    return isAdmin ? children : <Navigate to="/" replace />; // Redirect to homepage if not admin
};

export default ProtectedRoute;
