import React, { useState } from 'react';
import { useAuth } from './AuthContext';

const Events = () => {
    const { user, logout, events = [], addEvent } = useAuth(); // Default events to an empty array
    const [newEvent, setNewEvent] = useState({ title: '', address: '', date: '', image: '' });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewEvent({ ...newEvent, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        addEvent(newEvent); // Call addEvent from context
        setNewEvent({ title: '', address: '', date: '', image: '' }); // Reset form
    };

    const handleLogout = () => {
        logout();
    };

    return (
        <div>
            <h2>Upcoming Events</h2>
            {user && user.role === 'admin' && (
                <>
                    <button onClick={handleLogout} style={{ marginBottom: '10px' }}>
                        Logout
                    </button>
                    <h3>Add New Event</h3>
                    <form onSubmit={handleSubmit}>
                        <input
                            type="text"
                            name="title"
                            placeholder="Event Title"
                            value={newEvent.title}
                            onChange={handleInputChange}
                            required
                        />
                        <input
                            type="text"
                            name="address"
                            placeholder="Event Address"
                            value={newEvent.address}
                            onChange={handleInputChange}
                            required
                        />
                        <input
                            type="date"
                            name="date"
                            value={newEvent.date}
                            onChange={handleInputChange}
                            required
                        />
                        <input
                            type="text"
                            name="image"
                            placeholder="Image URL"
                            value={newEvent.image}
                            onChange={handleInputChange}
                            required
                        />
                        <button type="submit">Add Event</button>
                    </form>
                </>
            )}
            <h3>Event List</h3>
            <ul>
                {events.length > 0 ? (
                    events.map((event, index) => (
                        <li key={index}>
                            <h4>{event.title}</h4>
                            <p>{event.address}</p>
                            <p>{event.date}</p>
                            <img
                                src={event.image}
                                alt={event.title}
                                style={{ width: '100px', height: 'auto' }}
                            />
                        </li>
                    ))
                ) : (
                    <p>No events available.</p>
                )}
            </ul>
        </div>
    );
};

export default Events;
