import React, { useState } from 'react';

const AdminLogin = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [eventDetails, setEventDetails] = useState({ title: '', date: '', description: '', photo: '' });
    const [photoURL, setPhotoURL] = useState('');
    const [failedAttempts, setFailedAttempts] = useState(0);
    const [isResetPasswordVisible, setIsResetPasswordVisible] = useState(false);
    const [newPassword, setNewPassword] = useState('');

    const handleLogin = (event) => {
        event.preventDefault();
        if (username === 'admin' && password === 'admin') {
            setIsLoggedIn(true);
            setFailedAttempts(0);
            alert('Login successful!');
        } else {
            setFailedAttempts((prev) => prev + 1);
            alert('Invalid username or password');
            if (failedAttempts >= 2) {
                setIsResetPasswordVisible(true);
            }
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setPassword('');
        setEventDetails({ title: '', date: '', description: '', photo: '' });
        setPhotoURL('');
        alert('Logged out successfully!');
    };

    const handleEventChange = (e) => {
        const { name, value } = e.target;
        setEventDetails((prevDetails) => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handleEventSubmit = (e) => {
        e.preventDefault();
        console.log('Event Details:', eventDetails);
        alert('Event added successfully!');
        setEventDetails({ title: '', date: '', description: '', photo: '' });
    };

    const handlePhotoSubmit = (e) => {
        e.preventDefault();
        console.log('Photo URL:', photoURL);
        alert('Photo added successfully!');
        setPhotoURL('');
    };

    const handlePasswordReset = (e) => {
        e.preventDefault();
        console.log('New Password:', newPassword);
        alert('Password has been reset successfully!');
        setIsResetPasswordVisible(false);
        setNewPassword('');
        setIsLoggedIn(true);
        setUsername('admin');
        setPassword(newPassword);
    };

    return (
        <div>
            <h2>Admin Login</h2>
            {isLoggedIn ? (
                <div>
                    <h3>Welcome, Admin!</h3>
                    <button onClick={handleLogout}>Logout</button>
                    
                    {/* Event management form */}
                    <form onSubmit={handleEventSubmit}>
                        <h4>Add Event</h4>
                        <div>
                            <label htmlFor="title">Event Title:</label>
                            <input 
                                type="text" 
                                id="title" 
                                name="title" 
                                value={eventDetails.title} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="date">Event Date:</label>
                            <input 
                                type="date" 
                                id="date" 
                                name="date" 
                                value={eventDetails.date} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="description">Description:</label>
                            <textarea 
                                id="description" 
                                name="description" 
                                value={eventDetails.description} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="photo">Event Photo URL:</label>
                            <input 
                                type="url" 
                                id="photo" 
                                name="photo" 
                                value={eventDetails.photo} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <button type="submit">Add Event</button>
                    </form>
                    
                    {/* Photo and Video management form */}
                    <form onSubmit={handlePhotoSubmit}>
                        <h4>Add Photo or Video</h4>
                        <div>
                            <label htmlFor="photoURL">Photo/Video URL:</label>
                            <input 
                                type="url" 
                                id="photoURL" 
                                name="photoURL" 
                                value={photoURL} 
                                onChange={(e) => setPhotoURL(e.target.value)} 
                                required 
                            />
                        </div>
                        <button type="submit">Add Photo/Video</button>
                    </form>

                    {/* Membership Register View */}
                    <div>
                        <h4>Membership Register</h4>
                        <button onClick={() => alert('Viewing membership register...')}>
                            View Membership Register
                        </button>
                        {/* Logic to retrieve and display the register goes here */}
                    </div>
                </div>
            ) : (
                <form onSubmit={handleLogin}>
                    <div>
                        <label htmlFor="username">Username:</label>
                        <input 
                            type="text" 
                            id="username" 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)} 
                            required 
                        />
                    </div>
                    <div>
                        <label htmlFor="password">Password:</label>
                        <input 
                            type="password" 
                            id="password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>
            )}
            {isResetPasswordVisible && (
                <div>
                    <h4>Reset Password</h4>
                    <form onSubmit={handlePasswordReset}>
                        <div>
                            <label htmlFor="newPassword">New Password:</label>
                            <input 
                                type="password" 
                                id="newPassword" 
                                value={newPassword} 
                                onChange={(e) => setNewPassword(e.target.value)} 
                                required 
                            />
                        </div>
                        <button type="submit">Reset Password</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default AdminLogin;
