import React, { useState } from 'react';
import './AdminPanel.css';

const GalleryManagement = () => {
  const [images, setImages] = useState([
    {
      id: 1,
      title: 'Cultural Festival 2024',
      description: 'Highlights from our annual festival',
      url: '/images/festival.jpg',
      date: '2024-03-15'
    }
  ]);
  const [showForm, setShowForm] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    url: '',
    date: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (currentImage) {
      setImages(images.map(img =>
        img.id === currentImage.id
          ? { ...formData, id: img.id }
          : img
      ));
    } else {
      setImages([...images, { ...formData, id: Date.now() }]);
    }
    handleCloseForm();
  };

  const handleEdit = (image) => {
    setCurrentImage(image);
    setFormData(image);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this image?')) {
      setImages(images.filter(img => img.id !== id));
    }
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setCurrentImage(null);
    setFormData({
      title: '',
      description: '',
      url: '',
      date: ''
    });
  };

  return (
    <div className="admin-section">
      <div className="section-header">
        <h2>Gallery Management</h2>
        <button className="add-btn" onClick={() => setShowForm(true)}>
          Add New Image
        </button>
      </div>

      {showForm && (
        <div className="modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{currentImage ? 'Edit Image' : 'Add New Image'}</h3>
              <button className="close-btn" onClick={handleCloseForm}>&times;</button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="title">Title</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea
                  id="description"
                  name="description"
                  value={formData.description}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="url">Image URL</label>
                <input
                  type="text"
                  id="url"
                  name="url"
                  value={formData.url}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="date">Date</label>
                <input
                  type="date"
                  id="date"
                  name="date"
                  value={formData.date}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-actions">
                <button type="submit" className="submit-btn">
                  {currentImage ? 'Update Image' : 'Add Image'}
                </button>
                <button type="button" className="cancel-btn" onClick={handleCloseForm}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="gallery-grid">
        {images.map(image => (
          <div key={image.id} className="gallery-item">
            <img src={image.url} alt={image.title} />
            <div className="gallery-item-overlay">
              <h4>{image.title}</h4>
              <p>{image.description}</p>
              <div className="gallery-item-actions">
                <button className="edit-btn" onClick={() => handleEdit(image)}>
                  Edit
                </button>
                <button className="delete-btn" onClick={() => handleDelete(image.id)}>
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GalleryManagement;
