import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import culturalImage from '../images/Cultural.jpg';

const HeroSection = () => {
  return (
    <div className="hero-section">
      <Container>
        <Row className="align-items-center">
          <Col md={6} className="text-center text-md-start">
            <h1 className="display-4 fw-bold mb-4">Welcome to Edo Cultural Association</h1>
            <p className="lead mb-4">Celebrating and preserving our rich cultural heritage</p>
          </Col>
          <Col md={6}>
            <img
              src={culturalImage}
              alt="Edo Cultural"
              className="img-fluid rounded shadow-lg"
              style={{
                maxHeight: '400px',
                width: '100%',
                objectFit: 'cover'
              }}
            />
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default HeroSection;
