import React, { createContext, useContext, useEffect, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [error, setError] = useState(null);
    const [loginSuccess, setLoginSuccess] = useState(false);
    const [events, setEvents] = useState([]);

    // Load saved user from local storage on component mount
    useEffect(() => {
        const savedUser = JSON.parse(localStorage.getItem("user"));
        if (savedUser) {
            setUser(savedUser);
            setLoginSuccess(true);
        }
    }, []);

    // Login function
    const login = (username, password, rememberMe) => {
        return new Promise((resolve, reject) => {
            if (username === 'admin' && password === '') {
                const loggedInUser = { name: 'Admin', role: 'admin' };
                setUser(loggedInUser);
                setError(null);
                setLoginSuccess(true);

                // Store user in local storage if rememberMe is true
                if (rememberMe) {
                    localStorage.setItem("user", JSON.stringify(loggedInUser));
                }

                resolve();
            } else {
                setError('Invalid username or password');
                reject(new Error('Invalid credentials'));
            }
        });
    };

    // Logout function
    const logout = () => {
        setUser(null);
        setError(null);
        setLoginSuccess(false);
        localStorage.removeItem("user");
    };

    // Add a new event
    const addEvent = (event) => {
        setEvents((prevEvents) => [...prevEvents, event]);
    };

    // Remove an event by its index
    const removeEvent = (index) => {
        setEvents((prevEvents) => prevEvents.filter((_, i) => i !== index));
    };

    return (
        <AuthContext.Provider value={{ user, login, logout, error, loginSuccess, events, addEvent, removeEvent }}>
            {children}
        </AuthContext.Provider>
    );
};

// Custom hook to use the AuthContext
export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
