import React, { useState, useEffect } from 'react';

const Donation = () => {
    const [paymentMethod, setPaymentMethod] = useState('paypal'); // Default to PayPal
    const [donationAmount, setDonationAmount] = useState('');

    // Handle credit card payment processing
    const handleCardPayment = (event) => {
        event.preventDefault();
        // Implement actual credit card payment processing logic here
        alert(`Credit card donation of €${donationAmount} processed!`);
    };

    // PayPal SDK initialization
    useEffect(() => {
        const script = document.createElement('script');
        script.src = "https://www.paypal.com/sdk/js?client-id=YOUR_PAYPAL_CLIENT_ID";
        script.async = true;
        script.onload = () => {
            // PayPal Buttons
            window.paypal.Buttons({
                createOrder: function (data, actions) {
                    return actions.order.create({
                        purchase_units: [{
                            amount: {
                                value: donationAmount || '10.00' // Use donation amount or default to €10
                            }
                        }]
                    });
                },
                onApprove: function (data, actions) {
                    return actions.order.capture().then(function (details) {
                        alert('Donation of €' + donationAmount + ' completed by ' + details.payer.name.given_name);
                    });
                }
            }).render('#paypal-button-container');
        };
        document.body.appendChild(script);
    }, [donationAmount]); // Re-run effect when donationAmount changes

    return (
        <div>
            <h2>Support the Edo Cultural Association</h2>
            <p>Your contributions help us to preserve and promote Edo culture through various events and initiatives. Thank you for your support!</p>

            <h3>Make a Donation</h3>

            <div>
                <label>
                    <input
                        type="radio"
                        value="paypal"
                        checked={paymentMethod === 'paypal'}
                        onChange={() => setPaymentMethod('paypal')}
                    />
                    Donate via PayPal
                </label>
                <label>
                    <input
                        type="radio"
                        value="creditCard"
                        checked={paymentMethod === 'creditCard'}
                        onChange={() => setPaymentMethod('creditCard')}
                    />
                    Donate via Credit Card
                </label>
            </div>

            {paymentMethod === 'paypal' && (
                <>
                    <h4>PayPal Payment</h4>
                    <div id="paypal-button-container"></div>
                </>
            )}

            {paymentMethod === 'creditCard' && (
                <>
                    <h4>Credit Card Payment</h4>
                    <form onSubmit={handleCardPayment}>
                        <div>
                            <label htmlFor="name">Name:</label>
                            <input
                                type="text"
                                id="name"
                                required
                                onChange={(e) => setDonationAmount(e.target.value)}
                            />
                        </div>
                        <div>
                            <label htmlFor="email">Email:</label>
                            <input type="email" id="email" required />
                        </div>
                        <div>
                            <label htmlFor="amount">Donation Amount (€):</label>
                            <input
                                type="number"
                                id="amount"
                                required
                                min="1"
                                onChange={(e) => setDonationAmount(e.target.value)}
                            />
                        </div>
                        <button type="submit">Donate</button>
                    </form>
                </>
            )}
        </div>
    );
};

export default Donation;
