import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import MembershipRegister from './membershipRegister';
import Events from './events';
import PhotosVideo from './photosVideo';
import Donation from './donation';
import About from './about';
import Contact from './contact';
import AdminLogin from './adminlogin';
import './styles.css'; // Import your CSS styles
import { AuthProvider, useAuth } from './AuthProvider'; // Ensure this path is correct
import SocialMediaLinks from './SocialMediaLinks'; // Import the SocialMediaLinks component
import WhatsAppChat from './WhatsAppChat'; // Import the WhatsAppChat component

// Header component
function Header() {
    return (
        <header className="header">
            <div className="flag-container">
                <img 
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR1jFIfSvWwsEe-bf6k3_UDFN85CwtSHu9Puw&s" 
                    alt="Nigeria Flag" 
                    className="flag flag-left" 
                />
                <img 
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQkU0tjUdDG83Yt6Dxrya21YMN3L00LWB2Vew&s" 
                    alt="Italy Flag" 
                    className="flag flag-right" 
                />
            </div>
            <div className="logo-container">
                <Link to="/">
                    <img 
                        src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRJVPwfUMs4W-5MyRdUmAj_UMB2y8gxtpjBjw&s" 
                        alt="Edo Cultural Association Logo" 
                        className="logo" 
                    />
                </Link>
                <div className="header-text">
                    <h1>Edo Cultural Association</h1>
                    <p>Friuli Venezia Giulia, Udine, Italy</p>
                </div>
            </div>
            <SocialMediaLinks /> {/* Add the Social Media Links here */}
        </header>
    );
}

// Navigation component
function Nav() {
    const { user, logout } = useAuth(); // Access user information and logout function

    return (
        <nav className="navbar">
            <Link to="/">Homepage</Link>
            <Link to="/membership-register">Membership Register</Link>
            <Link to="/events">Events</Link>
            <Link to="/photos-video">Photos & Video</Link>
            <Link to="/donation">Donation</Link>
            <Link to="/about">About</Link>
            <Link to="/contact">Contact</Link>
            {!user && <Link to="/admin">Admin</Link>} {/* Show Admin link only if not authenticated */}
            {user && <button onClick={logout}>Logout</button>} {/* Logout button if user is logged in */}
        </nav>
    );
}

// Main component
function Main() {
    return (
        <main>
            <Routes>
                <Route path="/" element={<Homepage />} />
                <Route path="/membership-register" element={<MembershipRegister />} />
                <Route path="/events" element={<Events />} />
                <Route path="/photos-video" element={<PhotosVideo />} />
                <Route path="/donation" element={<Donation />} />
                <Route path="/about" element={<About />} />
                <Route path="/contact" element={<Contact />} />
                <Route path="/admin" element={<AdminLogin />} />
            </Routes>
        </main>
    );
}

// Homepage component
function Homepage() {
    return (
        <section className="homepage">
            <h2>Welcome to the Edo Cultural Association</h2>
            <div className="homepage-introduction">
                <p>Join us in celebrating our rich cultural heritage!</p>
            </div>
            <div className="image-gallery">
                <ImageSection title="Our Cultural Events" images={['image1.jpg', 'image2.jpg', 'image3.jpg', 'image4.jpg', 'image5.jpg']} />
                <ImageSection title="Traditional Celebrations" images={['image6.jpg', 'image7.jpg', 'image8.jpg', 'image9.jpg', 'image10.jpg']} />
                <ImageSection title="Art and Crafts" images={['image11.jpg', 'image12.jpg', 'image13.jpg', 'image14.jpg', 'image15.jpg']} />
            </div>
            <div className="video-container">
                <h3>Watch Our Cultural Highlights</h3>
                <iframe
                    width="600"
                    height="400"
                    src="https://www.youtube.com/embed/nUsjmh0ktno"
                    title="Cultural Highlights"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                ></iframe>
            </div>
            <div className="president-info">
                <h3>Meet Our President</h3>
                <p>President: King Luca</p>
                <p>Took office: January 2023</p>
                <img 
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSYN7adiaeZRZmYnfpa1HYXodVLcffWGRR08g&s" 
                    alt="President King Luca" 
                    className="president-picture" 
                />
            </div>
        </section>
    );
}

// ImageSection Component for better reusability
function ImageSection({ title, images }) {
    return (
        <div className="image-section">
            <h3>{title}</h3>
            <div className="gallery">
                {images.map((src, index) => (
                    <img 
                        key={index} 
                        src={src} 
                        alt={`${title} ${index + 1}`} 
                        className="fade-in" 
                        onError={(e) => { e.target.onerror = null; e.target.src = 'fallback-image.jpg'; }} // Add a fallback image
                    />
                ))}
            </div>
        </div>
    );
}

// Footer component
function Footer() {
    return (
        <footer>
            <div className="marquee">
                &copy; {new Date().getFullYear()} Edo Cultural Association. All rights reserved.
            </div>
        </footer>
    );
}

// Main App component
function App() {
    return (
        <AuthProvider>
            <Router>
                <div id="app">
                    {/* Flying birds background */}
                    <div className="birds-container">
                        <img src="/bird1.png" alt="Bird" className="bird" />
                        <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcROSKRHsaUkMcdzmBD2gujRxwfMUAd1dyNwVA&s" alt="Bird" className="bird" />
                        <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQcTgT0-TKIjpuywoC6G-APl0UywEKae4WL5g&s" alt="Bird" className="bird" />
                        <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRXXaa-lJtVPVRVvJMHbxPoS8NsqNoxo9rAFA&s" alt="Bird" className="bird" />
                    </div>
                    <Header />
                    <Nav />
                    <Main />
                    <WhatsAppChat /> {/* Add the WhatsApp chat button */}
                    <Footer />
                </div>
            </Router>
        </AuthProvider>
    );
}

export default App;
