import React, { useState } from 'react';
import { useAuth } from './AuthProvider';

const AdminLogin = () => {
    const { user, login, events, addEvent, removeEvent } = useAuth(); // Include login
    const [newEvent, setNewEvent] = useState({
        name: '',
        address: '',
        picture: '',
        location: '',
        map: '',
        signedBy: '',
        date: '',
        time: '',
        venue: ''
    });

    const [username, setUsername] = useState(''); // State for username
    const [password, setPassword] = useState(''); // State for password
    const [error, setError] = useState(''); // State for error messages

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewEvent((prev) => ({ ...prev, [name]: value }));
    };

    const handleLogin = async (e) => {
        e.preventDefault(); // Prevent default form submission
        setError(''); // Reset any previous error
        try {
            await login(username, password, false); // Call login from context
        } catch (err) {
            setError('Invalid username or password'); // Set error message
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        addEvent(newEvent); // Call addEvent from context
        setNewEvent({ name: '', address: '', picture: '', location: '', map: '', signedBy: '', date: '', time: '', venue: '' }); // Reset form
    };

    const handleRemove = (event) => {
        removeEvent(event); // Call removeEvent from context
    };

    return (
        <div>
            {!user ? (
                <div>
                    <h2>Admin Login</h2>
                    {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display error message */}
                    <form onSubmit={handleLogin}>
                        <input 
                            type="text" 
                            placeholder="Username" 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)} 
                            required 
                        />
                        <input 
                            type="password" 
                            placeholder="Password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                        <button type="submit">Login</button>
                    </form>
                </div>
            ) : (
                <>
                    <h3>Add New Event</h3>
                    <form onSubmit={handleSubmit}>
                        <input type="text" name="name" placeholder="Event Name" value={newEvent.name} onChange={handleInputChange} required />
                        <input type="text" name="address" placeholder="Event Address" value={newEvent.address} onChange={handleInputChange} required />
                        <input type="text" name="picture" placeholder="Event Picture URL" value={newEvent.picture} onChange={handleInputChange} required />
                        <input type="text" name="location" placeholder="Location" value={newEvent.location} onChange={handleInputChange} required />
                        <input type="text" name="map" placeholder="Map Link" value={newEvent.map} onChange={handleInputChange} required />
                        <input type="text" name="signedBy" placeholder="Signed By Chairman" value={newEvent.signedBy} onChange={handleInputChange} required />
                        <input type="date" name="date" value={newEvent.date} onChange={handleInputChange} required />
                        <input type="time" name="time" value={newEvent.time} onChange={handleInputChange} required />
                        <input type="text" name="venue" placeholder="Venue" value={newEvent.venue} onChange={handleInputChange} required />
                        <button type="submit">Add Event</button>
                    </form>

                    <h3>Event List</h3>
                    <ul>
                        {events.map((event, index) => (
                            <li key={index}>
                                <h4>{event.name}</h4>
                                <p>Address: {event.address}</p>
                                <p>Location: {event.location}</p>
                                <p>Date: {event.date}</p>
                                <p>Time: {event.time}</p>
                                <p>Signed By: {event.signedBy}</p>
                                <img src={event.picture} alt={event.name} style={{ width: '100px', height: 'auto' }} />
                                <button onClick={() => handleRemove(event)}>Remove Event</button>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default AdminLogin;
