import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Modal } from 'react-bootstrap';
import './AdminPanel.css';

const GalleryManagement = () => {
  const [gallery, setGallery] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [newImage, setNewImage] = useState({
    file: null,
    caption: '',
    category: ''
  });

  useEffect(() => {
    // Load gallery from localStorage
    const storedGallery = JSON.parse(localStorage.getItem('gallery') || '[]');
    setGallery(storedGallery);
  }, []);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewImage(prev => ({
          ...prev,
          file: reader.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAddImage = (e) => {
    e.preventDefault();

    if (!newImage.file || !newImage.caption) {
      alert('Please select an image and add a caption');
      return;
    }

    const image = {
      id: Date.now(),
      url: newImage.file,
      caption: newImage.caption,
      category: newImage.category,
      uploadDate: new Date().toISOString()
    };

    const updatedGallery = [...gallery, image];
    setGallery(updatedGallery);
    localStorage.setItem('gallery', JSON.stringify(updatedGallery));

    setNewImage({
      file: null,
      caption: '',
      category: ''
    });

    alert('Image added successfully!');
  };

  const handleEditImage = (image) => {
    setSelectedImage(image);
    setShowModal(true);
  };

  const handleUpdateImage = () => {
    const updatedGallery = gallery.map(img => 
      img.id === selectedImage.id ? selectedImage : img
    );

    setGallery(updatedGallery);
    localStorage.setItem('gallery', JSON.stringify(updatedGallery));
    setShowModal(false);
    alert('Image updated successfully!');
  };

  const handleDeleteImage = (imageId) => {
    if (window.confirm('Are you sure you want to delete this image?')) {
      const updatedGallery = gallery.filter(img => img.id !== imageId);
      setGallery(updatedGallery);
      localStorage.setItem('gallery', JSON.stringify(updatedGallery));
    }
  };

  return (
    <Container className="py-5">
      <div className="admin-section">
        <h2 className="admin-section-title">
          <i className="fas fa-images"></i>
          Gallery Management
        </h2>

        <Row>
          <Col md={4}>
            <div className="admin-card">
              <h3 className="management-title">Upload New Image</h3>
              <Form onSubmit={handleAddImage}>
                <Form.Group className="mb-3">
                  <Form.Label>Select Image*</Form.Label>
                  <Form.Control
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Caption*</Form.Label>
                  <Form.Control
                    type="text"
                    value={newImage.caption}
                    onChange={(e) => setNewImage({...newImage, caption: e.target.value})}
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Category</Form.Label>
                  <Form.Control
                    type="text"
                    value={newImage.category}
                    onChange={(e) => setNewImage({...newImage, category: e.target.value})}
                    placeholder="e.g., Events, Cultural, Members"
                  />
                </Form.Group>

                <Button type="submit" variant="primary" className="w-100">
                  Upload Image
                </Button>
              </Form>
            </div>
          </Col>

          <Col md={8}>
            <div className="admin-card">
              <h3 className="management-title">Gallery Images</h3>
              <Row xs={1} md={2} lg={3} className="g-4">
                {gallery.map(image => (
                  <Col key={image.id}>
                    <Card className="h-100">
                      <Card.Img
                        variant="top"
                        src={image.url}
                        style={{ height: '200px', objectFit: 'cover' }}
                      />
                      <Card.Body>
                        <Card.Title className="h6">{image.caption}</Card.Title>
                        {image.category && (
                          <Card.Text className="text-muted small">
                            Category: {image.category}
                          </Card.Text>
                        )}
                        <div className="d-flex gap-2 mt-3">
                          <Button
                            variant="outline-primary"
                            size="sm"
                            onClick={() => handleEditImage(image)}
                          >
                            Edit
                          </Button>
                          <Button
                            variant="outline-danger"
                            size="sm"
                            onClick={() => handleDeleteImage(image.id)}
                          >
                            Delete
                          </Button>
                        </div>
                      </Card.Body>
                    </Card>
                  </Col>
                ))}
              </Row>

              {gallery.length === 0 && (
                <div className="text-center py-5">
                  <p className="text-muted mb-0">No images in the gallery</p>
                </div>
              )}
            </div>
          </Col>
        </Row>
      </div>

      {/* Edit Image Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Image Details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Caption</Form.Label>
              <Form.Control
                type="text"
                value={selectedImage?.caption || ''}
                onChange={(e) => setSelectedImage({...selectedImage, caption: e.target.value})}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Category</Form.Label>
              <Form.Control
                type="text"
                value={selectedImage?.category || ''}
                onChange={(e) => setSelectedImage({...selectedImage, category: e.target.value})}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleUpdateImage}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default GalleryManagement;
