import React from 'react';

const Donation = () => {
    const handleCardPayment = (event) => {
        event.preventDefault();
        // Handle credit card payment processing here
        alert('Credit card donation processed!');
    };

    return (
        <div>
            <h2>Support the Edo Cultural Association</h2>
            <p>Your contributions help us to preserve and promote Edo culture through various events and initiatives. Thank you for your support!</p>

            <h3>Make a Donation</h3>

            <h4>Donate via PayPal</h4>
            <div id="paypal-button-container"></div>
            
            <h4>Or Donate via Credit Card</h4>
            <form onSubmit={handleCardPayment}>
                <div>
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name" required />
                </div>
                <div>
                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" required />
                </div>
                <div>
                    <label htmlFor="amount">Donation Amount (â‚¬):</label>
                    <input type="number" id="amount" required min="1" />
                </div>
                <button type="submit">Donate</button>
            </form>

            {/* PayPal SDK Script */}
            <script src="https://www.paypal.com/sdk/js?client-id=YOUR_PAYPAL_CLIENT_ID"></script>
            <script>
                {`
                    paypal.Buttons({
                        createOrder: function(data, actions) {
                            return actions.order.create({
                                purchase_units: [{
                                    amount: {
                                        value: '10.00' // Set this to your desired donation amount
                                    }
                                }]
                            });
                        },
                        onApprove: function(data, actions) {
                            return actions.order.capture().then(function(details) {
                                alert('Donation completed by ' + details.payer.name.given_name);
                            });
                        }
                    }).render('#paypal-button-container');
                `}
            </script>
        </div>
    );
};

export default Donation;
