import React, { useState } from 'react';
import { useAuth } from './AuthProvider';

const AdminLogin = () => {
    const { user, login, events, addEvent, removeEvent } = useAuth();
    const [newEvent, setNewEvent] = useState({
        name: '',
        address: '',
        picture: null,
        location: '',
        signedBy: '',
        date: ''
    });

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewEvent((prev) => ({ ...prev, [name]: value }));
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setNewEvent((prev) => ({ ...prev, picture: file })); // Store the file object
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        try {
            await login(username, password, false);
        } catch (err) {
            setError('Invalid username or password');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const eventWithFile = {
            ...newEvent,
            picture: newEvent.picture
        };

        addEvent(eventWithFile);
        setNewEvent({ name: '', address: '', picture: null, location: '', signedBy: '', date: '' }); // Reset form
    };

    const handleRemove = (event) => {
        removeEvent(event); // Call removeEvent from context
    };

    return (
        <div>
            {!user ? (
                <div>
                    <h2>Admin Login</h2>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <form onSubmit={handleLogin}>
                        <input 
                            type="text" 
                            placeholder="Username" 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)} 
                            required 
                        />
                        <input 
                            type="password" 
                            placeholder="Password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                        <button type="submit">Login</button>
                    </form>
                </div>
            ) : (
                <>
                    <h3>Add New Event</h3>
                    <form onSubmit={handleSubmit}>
                        <input type="text" name="name" placeholder="Event Name" value={newEvent.name} onChange={handleInputChange} required />
                        <input type="text" name="address" placeholder="Event Address" value={newEvent.address} onChange={handleInputChange} required />
                        <input type="file" name="picture" accept="image/*" onChange={handleFileChange} required /> {/* File input for image */}
                        <input type="text" name="location" placeholder="Location" value={newEvent.location} onChange={handleInputChange} required />
                        <input type="text" name="signedBy" placeholder="Signed By Chairman" value={newEvent.signedBy} onChange={handleInputChange} required />
                        <input type="date" name="date" value={newEvent.date} onChange={handleInputChange} required />
                        <button type="submit">Add Event</button>
                    </form>

                    <h3>Event List</h3>
                    <ul>
                        {events.map((event, index) => (
                            <li key={index}>
                                <h4>{event.name}</h4>
                                <p>Address: {event.address}</p>
                                <p>Location: {event.location}</p>
                                <p>Date: {event.date}</p>
                                <p>Signed By: {event.signedBy}</p>
                                {event.picture && <img src={URL.createObjectURL(event.picture)} alt={event.name} style={{ width: '100px', height: 'auto' }} />} {/* Display image */}
                                <button onClick={() => handleRemove(event)}>Remove Event</button>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default AdminLogin;
