import React, { useState } from 'react';
import { useAuth } from './AuthProvider';

const AdminLogin = () => {
    const { user, login, events, addEvent, removeEvent, members, addMember } = useAuth(); // Keeping event management and adding member management
    const [newEvent, setNewEvent] = useState({
        name: '',
        address: '',
        picture: '',
        location: '',
        signedBy: '',
        date: '',
        venue: ''
    });

    const [newMember, setNewMember] = useState({
        surname: '',
        name: '',
        dob: '',
        town: '',
        localGovernment: '',
        passportNumber: '',
        codiceFiscale: '',
        phone: '',
        maritalStatus: '',
        parentalStatus: '',
        clubMembershipDetails: '',
        criminalRecords: '',
        reasonForEnrolling: ''
    });

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);
    const [error, setError] = useState('');

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewEvent((prev) => ({ ...prev, [name]: value }));
    };

    const handleMemberChange = (e) => {
        const { name, value } = e.target;
        setNewMember((prev) => ({ ...prev, [name]: value }));
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        try {
            await login(username, password, rememberMe);
        } catch (err) {
            setError('Invalid username or password');
        }
    };

    const handleEventSubmit = (e) => {
        e.preventDefault();
        addEvent(newEvent); // Use addEvent from context
        setNewEvent({ name: '', address: '', picture: '', location: '', signedBy: '', date: '', venue: '' }); // Reset form
    };

    const handleMemberSubmit = (e) => {
        e.preventDefault();
        addMember(newMember); // Use addMember from context
        setNewMember({
            surname: '',
            name: '',
            dob: '',
            town: '',
            localGovernment: '',
            passportNumber: '',
            codiceFiscale: '',
            phone: '',
            maritalStatus: '',
            parentalStatus: '',
            clubMembershipDetails: '',
            criminalRecords: '',
            reasonForEnrolling: ''
        }); // Reset member form
    };

    const handleRemove = (event) => {
        removeEvent(event); // Use removeEvent from context
    };

    return (
        <div>
            {!user ? (
                <div>
                    <h2>Admin Login</h2>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <form onSubmit={handleLogin}>
                        <input 
                            type="text" 
                            placeholder="Username" 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)} 
                            required 
                        />
                        <input 
                            type="password" 
                            placeholder="Password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                        <label>
                            <input 
                                type="checkbox" 
                                checked={rememberMe} 
                                onChange={(e) => setRememberMe(e.target.checked)} 
                            />
                            Remember Me
                        </label>
                        <button type="submit">Login</button>
                    </form>
                </div>
            ) : (
                <>
                    <h3>Add New Event</h3>
                    <form onSubmit={handleEventSubmit}>
                        <input type="text" name="name" placeholder="Event Name" value={newEvent.name} onChange={handleInputChange} required />
                        <input type="text" name="address" placeholder="Event Address" value={newEvent.address} onChange={handleInputChange} required />
                        <input type="file" name="picture" placeholder="Event Picture" onChange={(e) => setNewEvent({ ...newEvent, picture: URL.createObjectURL(e.target.files[0]) })} required />
                        <input type="text" name="location" placeholder="Location" value={newEvent.location} onChange={handleInputChange} required />
                        <input type="text" name="signedBy" placeholder="Signed By Chairman" value={newEvent.signedBy} onChange={handleInputChange} required />
                        <input type="date" name="date" value={newEvent.date} onChange={handleInputChange} required />
                        <input type="text" name="venue" placeholder="Venue" value={newEvent.venue} onChange={handleInputChange} required />
                        <button type="submit">Add Event</button>
                    </form>

                    <h3>Event List</h3>
                    <ul>
                        {events.map((event, index) => (
                            <li key={index}>
                                <h4>{event.name}</h4>
                                <p>Address: {event.address}</p>
                                <p>Location: {event.location}</p>
                                <p>Date: {event.date}</p>
                                <p>Signed By: {event.signedBy}</p>
                                {event.picture && <img src={event.picture} alt={event.name} style={{ width: '100px', height: 'auto' }} />}
                                <button onClick={() => handleRemove(event)}>Remove Event</button>
                            </li>
                        ))}
                    </ul>

                    <h3>Add New Member</h3>
                    <form onSubmit={handleMemberSubmit}>
                        <input type="text" name="surname" placeholder="Surname" value={newMember.surname} onChange={handleMemberChange} required />
                        <input type="text" name="name" placeholder="Name" value={newMember.name} onChange={handleMemberChange} required />
                        <input type="date" name="dob" placeholder="Date of Birth" value={newMember.dob} onChange={handleMemberChange} required />
                        <input type="text" name="town" placeholder="Town/City of Birth" value={newMember.town} onChange={handleMemberChange} required />
                        <input type="text" name="localGovernment" placeholder="Local Government" value={newMember.localGovernment} onChange={handleMemberChange} required />
                        <input type="text" name="passportNumber" placeholder="Passport/ID Number" value={newMember.passportNumber} onChange={handleMemberChange} required />
                        <input type="text" name="codiceFiscale" placeholder="Codice Fiscale" value={newMember.codiceFiscale} onChange={handleMemberChange} required />
                        <input type="text" name="phone" placeholder="Phone/WhatsApp Number" value={newMember.phone} onChange={handleMemberChange} required />
                        <input type="text" name="maritalStatus" placeholder="Marital Status" value={newMember.maritalStatus} onChange={handleMemberChange} required />
                        <input type="text" name="parentalStatus" placeholder="Parental Status" value={newMember.parentalStatus} onChange={handleMemberChange} required />
                        <input type="text" name="clubMembershipDetails" placeholder="Association/Club Membership Details" value={newMember.clubMembershipDetails} onChange={handleMemberChange} required />
                        <input type="text" name="criminalRecords" placeholder="Criminal Records" value={newMember.criminalRecords} onChange={handleMemberChange} required />
                        <input type="text" name="reasonForEnrolling" placeholder="Reason for Enrolling" value={newMember.reasonForEnrolling} onChange={handleMemberChange} required />
                        <button type="submit">Add Member</button>
                    </form>

                    <h3>Registered Members</h3>
                    <ul>
                        {members.map((member, index) => (
                            <li key={index}>
                                <h4>{member.name} {member.surname}</h4>
                                <p>Date of Birth: {member.dob}</p>
                                <p>Town: {member.town}</p>
                                <p>Local Government: {member.localGovernment}</p>
                                <p>Phone: {member.phone}</p>
                                <p>Marital Status: {member.maritalStatus}</p>
                                <p>Parental Status: {member.parentalStatus}</p>
                                <p>Club Membership: {member.clubMembershipDetails}</p>
                                <p>Criminal Records: {member.criminalRecords}</p>
                                <p>Reason for Enrolling: {member.reasonForEnrolling}</p>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default AdminLogin;
