import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';

const HeroSection = () => {
  return (
    <div className="hero-section">
      <Container>
        <Row className="align-items-center">
          <Col md={6} className="text-center text-md-start">
            <h1 className="display-4 fw-bold mb-4">Welcome to Edo Cultural Association Italy</h1>
            <p className="lead mb-4">Preserving and promoting Edo culture in Italy through education, events, and community engagement.</p>
            <div className="d-flex gap-3 justify-content-center justify-content-md-start">
              <Button variant="primary" size="lg" className="px-4 py-2">
                Join Us <i className="fas fa-arrow-right ms-2"></i>
              </Button>
              <Button variant="outline-primary" size="lg" className="px-4 py-2">
                Learn More
              </Button>
            </div>
          </Col>
          <Col md={6}>
            <div className="position-relative">
              <img
                src="/images/portrait.jpg"
                alt="Edo Cultural Heritage"
                className="img-fluid rounded shadow-lg"
                style={{
                  maxHeight: '400px',
                  width: '100%',
                  objectFit: 'cover'
                }}
              />
              <div className="position-absolute bottom-0 start-0 end-0 p-3 text-center text-white" style={{
                background: 'linear-gradient(to top, rgba(0,0,0,0.7), transparent)',
                borderBottomLeftRadius: '0.5rem',
                borderBottomRightRadius: '0.5rem'
              }}>
                <h5 className="mb-0">Edo Cultural Heritage</h5>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default HeroSection;
