import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import MembershipRegister from './membershipRegister';
import Events from './events';
import PhotosVideo from './photosVideo';
import Donation from './donation';
import About from './about';  
import Contact from './contact';
import AdminLogin from './adminlogin';
import EventManagement from './EventManagement'; // Ensure this path is correct
import './styles.css'; // Import your CSS styles
import { AuthProvider, useAuth } from './AuthContext'; // Ensure this path is correct

// Header component
function Header() {
    return (
        <header>
            <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR1jFIfSvWwsEe-bf6k3_UDFN85CwtSHu9Puw&s" alt="Nigeria Flag" className="flag flag-left" />
            <img 
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQkU0tjUdDG83Yt6Dxrya21YMN3L00LWB2Vew&s" 
                alt="Italy Flag" 
                className="flag flag-right" 
            />
            <a href="/">
                <img 
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRJVPwfUMs4W-5MyRdUmAj_UMB2y8gxtpjBjw&s" 
                    alt="Edo Cultural Association Logo" 
                    className="logo" 
                />
            </a>
            <h1>Edo Cultural Association</h1>
            <p>Friuli Venezia Giulia Udine Italy</p>
        </header>
    );
}

// Navigation component
function Nav() {
    const { isAuthenticated } = useAuth(); // Access authentication status

    return (
        <nav>
            <Link to="/">Homepage</Link>
            <Link to="/membership-register">Membership Register</Link>
            <Link to="/events">Events</Link>
            <Link to="/photos-video">Photos & Video</Link>
            <Link to="/donation">Donation</Link>
            <Link to="/about">About</Link>
            <Link to="/contact">Contact</Link>
            {!isAuthenticated && <Link to="/admin">Admin</Link>} {/* Show Admin link only if not authenticated */}
            {isAuthenticated && <Link to="/event-management">Event Management</Link>} {/* Show this link if authenticated */}
        </nav>
    );
}

// Main component
function Main() {
    return (
        <main>
            <Routes>
                <Route path="/" element={<Homepage />} />
                <Route path="/membership-register" element={<MembershipRegister />} />
                <Route path="/events" element={<Events />} />
                <Route path="/photos-video" element={<PhotosVideo />} />
                <Route path="/donation" element={<Donation />} />
                <Route path="/about" element={<About />} />
                <Route path="/contact" element={<Contact />} />
                <Route path="/admin" element={<AdminLogin />} />
                <Route path="/event-management" element={<EventManagement />} /> {/* Route for event management */}
            </Routes>
        </main>
    );
}

// Dummy component for the homepage
function Homepage() {
    return (
        <section id="homepage">
            <h2>Welcome to Edo Cultural Association</h2>
            <img 
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTtnKhmACeNBgQY7S0Z_Cskszra7yL-h2R5LQ&s" 
                alt="Cultural Event Overview" 
                className="homepage-image" 
            />

            {/* Image Sections */}
            <ImageSection title="Our Cultural Events" images={[
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTtnKhmACeNBgQY7S0Z_Cskszra7yL-h2R5LQ&s",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS_Eo9t5DrqDLWTc85y77vtrLcfNLWD38Zmag&s",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSiQ9Rg6-oTnU49ALoLvkxFkZVRUeY8kzvwFQ&s"
            ]} />
            <ImageSection title="Traditional Celebrations" images={[
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQId9DJJIvnX7bLhIUL8bbcRhSZCLK0xAEumw&s",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTOsPnmoOLhaady5ouPuVTepFeSJVlhVh5UFw&s",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQkU0tjUdDG83Yt6Dxrya21YMN3L00LWB2Vew&s"
            ]} />
            <ImageSection title="Art and Crafts" images={[
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQId9DJJIvnX7bLhIUL8bbcRhSZCLK0xAEumw&s",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSiQ9Rg6-oTnU49ALoLvkxFkZVRUeY8kzvwFQ&s",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSYN7adiaeZRZmYnfpa1HYXodVLcffWGRR08g&s"
            ]} />

            {/* Video Section */}
            <div className="video-container">
                <h3>Watch Our Cultural Highlights</h3>
                <iframe
                    width="600"
                    height="400"
                    src="https://www.youtube.com/embed/nUsjmh0ktno" // Correct YouTube embed URL
                    title="Cultural Highlights"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                ></iframe>
            </div>

            {/* President's Information */}
            <div className="president-info">
                <h3>President: King Luca</h3>
                <p>Took office: January 2023</p>
                <img 
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSYN7adiaeZRZmYnfpa1HYXodVLcffWGRR08g&s" 
                    alt="President King Luca" 
                    className="president-picture" 
                />
            </div>
        </section>
    );
}

// ImageSection Component for better reusability
function ImageSection({ title, images }) {
    return (
        <div className="image-section">
            <h3>{title}</h3>
            <div className="gallery">
                {images.map((src, index) => (
                    <img 
                        key={index} 
                        src={src} 
                        alt={`${title} ${index + 1}`} 
                        className="fade-in" 
                    />
                ))}
            </div>
        </div>
    );
}

// Footer component
function Footer() {
    return (
        <footer>
            <div className="marquee">
                &copy; {new Date().getFullYear()} Edo Cultural Association. All rights reserved.
            </div>
        </footer>
    );
}

function App() {
    return (
        <AuthProvider> {/* Wrap the app with AuthProvider */}
            <Router>
                <div id="app"> {/* Add an ID for styling */}
                    <Header />
                    <Nav />
                    <Main />
