import React, { useState } from 'react';
import './AdminPanel.css';

const NewsManagement = () => {
  const [news, setNews] = useState([
    {
      id: 1,
      title: 'Cultural Association Launches New Initiative',
      content: 'We are excited to announce our new cultural exchange program...',
      date: '2024-03-15',
      author: 'Admin'
    }
  ]);
  const [showForm, setShowForm] = useState(false);
  const [currentArticle, setCurrentArticle] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    date: '',
    author: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (currentArticle) {
      setNews(news.map(article =>
        article.id === currentArticle.id
          ? { ...formData, id: article.id }
          : article
      ));
    } else {
      setNews([...news, { ...formData, id: Date.now() }]);
    }
    handleCloseForm();
  };

  const handleEdit = (article) => {
    setCurrentArticle(article);
    setFormData(article);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this article?')) {
      setNews(news.filter(article => article.id !== id));
    }
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setCurrentArticle(null);
    setFormData({
      title: '',
      content: '',
      date: '',
      author: ''
    });
  };

  return (
    <div className="admin-section">
      <div className="section-header">
        <h2>News Management</h2>
        <button className="add-btn" onClick={() => setShowForm(true)}>
          Add New Article
        </button>
      </div>

      {showForm && (
        <div className="modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{currentArticle ? 'Edit Article' : 'Add New Article'}</h3>
              <button className="close-btn" onClick={handleCloseForm}>&times;</button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="title">Title</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="content">Content</label>
                <textarea
                  id="content"
                  name="content"
                  value={formData.content}
                  onChange={handleInputChange}
                  required
                  rows="6"
                />
              </div>
              <div className="form-group">
                <label htmlFor="date">Date</label>
                <input
                  type="date"
                  id="date"
                  name="date"
                  value={formData.date}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="author">Author</label>
                <input
                  type="text"
                  id="author"
                  name="author"
                  value={formData.author}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-actions">
                <button type="submit" className="submit-btn">
                  {currentArticle ? 'Update Article' : 'Add Article'}
                </button>
                <button type="button" className="cancel-btn" onClick={handleCloseForm}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="news-grid">
        {news.map(article => (
          <div key={article.id} className="news-item">
            <h3>{article.title}</h3>
            <p className="news-meta">
              By {article.author} on {new Date(article.date).toLocaleDateString()}
            </p>
            <p className="news-content">{article.content}</p>
            <div className="news-actions">
              <button className="edit-btn" onClick={() => handleEdit(article)}>
                Edit
              </button>
              <button className="delete-btn" onClick={() => handleDelete(article.id)}>
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default NewsManagement;
