import React, { useState } from 'react';

const Donation = () => {
    const [paymentMethod, setPaymentMethod] = useState('paypal'); // Default payment method
    const [cardDetails, setCardDetails] = useState({
        name: '',
        number: '',
        cvv: '',
        address: '',
        email: ''
    });
    const [amount, setAmount] = useState(1); // Default donation amount
    const [paypalEmail, setPaypalEmail] = useState(''); // For PayPal email
    const [error, setError] = useState('');

    const handleCardPayment = (event) => {
        event.preventDefault();
        // Perform validation for credit card payment
        if (!cardDetails.name || !cardDetails.number || !cardDetails.cvv || !cardDetails.address) {
            setError('Please fill in all credit card fields.');
            return;
        }
        // Handle credit card payment processing here (e.g., call your backend)
        alert('Credit card donation processed!');
        // Reset form
        setCardDetails({ name: '', number: '', cvv: '', address: '', email: '' });
        setAmount(1);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name in cardDetails) {
            setCardDetails((prev) => ({ ...prev, [name]: value }));
        } else {
            setAmount(value);
        }
    };

    const handlePaypalEmailChange = (e) => {
        setPaypalEmail(e.target.value);
    };

    const handlePayPalProceed = () => {
        if (!paypalEmail) {
            setError('Please enter your PayPal email.');
            return;
        }
        alert(`Proceeding with donation via PayPal for ${paypalEmail}`);
        // Here you would typically integrate the PayPal button/render logic
    };

    return (
        <div>
            <h2>Support the Edo Cultural Association</h2>
            <p>Your contributions help us to preserve and promote Edo culture through various events and initiatives. Thank you for your support!</p>

            <h3>Make a Donation</h3>

            <div>
                <label>
                    <input 
                        type="radio" 
                        value="paypal" 
                        checked={paymentMethod === 'paypal'} 
                        onChange={() => setPaymentMethod('paypal')} 
                    />
                    Donate via PayPal
                </label>
                <label>
                    <input 
                        type="radio" 
                        value="creditCard" 
                        checked={paymentMethod === 'creditCard'} 
                        onChange={() => setPaymentMethod('creditCard')} 
                    />
                    Donate via Credit Card
                </label>
            </div>

            {paymentMethod === 'paypal' ? (
                <>
                    <div>
                        <label htmlFor="paypalEmail">PayPal Email:</label>
                        <input 
                            type="email" 
                            id="paypalEmail" 
                            value={paypalEmail} 
                            onChange={handlePaypalEmailChange} 
                            required 
                        />
                        <button 
                            type="button" 
                            onClick={handlePayPalProceed} 
                            style={{ marginLeft: '10px' }}
                        >
                            <img 
                                src="https://example.com/icon.png" // Replace with your icon URL
                                alt="Proceed"
                                style={{ width: '24px', height: '24px' }}
                            />
                        </button>
                    </div>
                    <div id="paypal-button-container"></div>
                    {/* PayPal SDK Script */}
                    <script src="https://www.paypal.com/sdk/js?client-id=YOUR_PAYPAL_CLIENT_ID"></script>
                    <script>
                        {`
                            paypal.Buttons({
                                createOrder: function(data, actions) {
                                    return actions.order.create({
                                        purchase_units: [{
                                            amount: {
                                                value: ${amount} // Set this to your desired donation amount
                                            }
                                        }]
                                    });
                                },
                                onApprove: function(data, actions) {
                                    return actions.order.capture().then(function(details) {
                                        alert('Donation completed by ' + details.payer.name.given_name);
                                    });
                                }
                            }).render('#paypal-button-container');
                        `}
                    </script>
                </>
            ) : (
                <form onSubmit={handleCardPayment}>
                    <div>
                        <label htmlFor="name">Name:</label>
                        <input 
                            type="text" 
                            id="name" 
                            name="name" 
                            value={cardDetails.name} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div>
                        <label htmlFor="number">Credit Card Number:</label>
                        <input 
                            type="text" 
                            id="number" 
                            name="number" 
                            value={cardDetails.number} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div>
                        <label htmlFor="cvv">CVV:</label>
                        <input 
                            type="text" 
                            id="cvv" 
                            name="cvv" 
                            value={cardDetails.cvv} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div>
                        <label htmlFor="address">Billing Address:</label>
                        <input 
                            type="text" 
                            id="address" 
                            name="address" 
                            value={cardDetails.address} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div>
                        <label htmlFor="email">Email:</label>
                        <input 
                            type="email" 
                            id="email" 
                            name="email" 
                            value={cardDetails.email} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div>
                        <label htmlFor="amount">Donation Amount (€):</label>
                        <input 
                            type="number" 
                            id="amount" 
                            value={amount} 
                            onChange={handleChange} 
                            required 
                            min="1" 
                        />
                    </div>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <button type="submit">Donate</button>
                </form>
            )}
        </div>
    );
};

export default Donation;
