import React, { useState } from 'react';
import { useAuth } from './AuthContext';
import { useNavigate } from 'react-router-dom';

function AdminLogin() {
    const { login, error, loginSuccess } = useAuth();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [loginError, setLoginError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await login(username, password);
            setLoginError(''); // Clear error on success
            navigate('/'); // Redirect to homepage or dashboard after successful login
        } catch (err) {
            setLoginError(error || 'An unexpected error occurred');
        }
    };

    return (
        <div>
            <h2>Admin Login</h2>
            {loginSuccess && <p style={{ color: 'green' }}>Login successful!</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Login</button>
                {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
            </form>
        </div>
    );
}

export default AdminLogin;
