import React from 'react';
import './Events.css';

const Events = () => {
  const events = [
    {
      id: 1,
      title: 'Annual Cultural Festival',
      date: '2024-06-15',
      location: 'Udine City Center',
      description: 'Join us for our annual celebration of Edo culture featuring traditional music, dance, and food.',
      image: '/images/cultural-festival.jpg'
    },
    {
      id: 2,
      title: 'Community Meeting',
      date: '2024-04-20',
      location: 'Community Center',
      description: 'Monthly community gathering to discuss important matters and strengthen our bonds.',
      image: '/images/community-meeting.jpg'
    },
    // Add more events as needed
  ];

  return (
    <div className="events-page">
      <h1 className="section-title">Upcoming Events</h1>
      <div className="events-grid">
        {events.map(event => (
          <div key={event.id} className="event-card">
            <div className="event-image">
              <img src={event.image} alt={event.title} onError={(e) => {
                e.target.src = '/images/default-event.jpg';
              }} />
            </div>
            <div className="event-content">
              <h3>{event.title}</h3>
              <p className="event-date">
                <i className="far fa-calendar"></i>
                {new Date(event.date).toLocaleDateString()}
              </p>
              <p className="event-location">
                <i className="fas fa-map-marker-alt"></i>
                {event.location}
              </p>
              <p className="event-description">{event.description}</p>
              <button className="btn">Learn More</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
