import React, { useState } from 'react';
import './MembershipRegister.css'; // Import the CSS file

const MembershipRegister = () => {
    // Initialize state for form data and messages
    const [formData, setFormData] = useState({
        surname: '',
        name: '',
        otherName: '',
        homeAddress: '',
        dob: '',
        townCity: '',
        localGovt: '',
        passportId: '',
        codiceFiscale: '',
        phone: '',
        nextOfKinName: '',
        nextOfKinPhone: '',
        nextOfKinAddress: '',
        nextOfKinCity: '',
        nextOfKinCountry: '',
        isMarried: '',
        partnerName: '',
        childrenCount: '',
        childrenNames: '',
        parentsStatus: '',
        association: '',
        criminalRecord: '',
        reason: '',
        agreement: false,
        passportPhoto: null,
    });

    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    // Handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    // Handle file input change
    const handleFileChange = (e) => {
        setFormData((prev) => ({
            ...prev,
            passportPhoto: e.target.files[0], // Save the file
        }));
    };

    // Handle checkbox changes
    const handleCheckboxChange = () => {
        setFormData((prev) => ({
            ...prev,
            agreement: !prev.agreement,
        }));
    };

    // Validate form data
    const validateFormData = () => {
        if (!formData.agreement) {
            setErrorMessage('You must agree to the terms and conditions.');
            return false;
        }
        // Add any additional validation checks as needed
        return true;
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateFormData()) return; // Validate before proceeding

        const formDataToSubmit = new FormData();
        for (const key in formData) {
            formDataToSubmit.append(key, formData[key]);
        }

        try {
            const response = await fetch('/api/membership-register', {
                method: 'POST',
                body: formDataToSubmit,
            });

            if (!response.ok) {
                const errorResponse = await response.json();
                throw new Error(errorResponse.message || 'Failed to submit the form. Please check your input.');
            }

            // Reset form data after successful submission
            setFormData({
                surname: '',
                name: '',
                otherName: '',
                homeAddress: '',
                dob: '',
                townCity: '',
                localGovt: '',
                passportId: '',
                codiceFiscale: '',
                phone: '',
                nextOfKinName: '',
                nextOfKinPhone: '',
                nextOfKinAddress: '',
                nextOfKinCity: '',
                nextOfKinCountry: '',
                isMarried: '',
                partnerName: '',
                childrenCount: '',
                childrenNames: '',
                parentsStatus: '',
                association: '',
                criminalRecord: '',
                reason: '',
                agreement: false,
                passportPhoto: null,
            });

            setSuccessMessage('Membership registration successful!');
            setErrorMessage('');

            // Clear success message after 5 seconds
            setTimeout(() => setSuccessMessage(''), 5000);
        } catch (error) {
            console.error('Error:', error);
            setErrorMessage(error.message);
            setSuccessMessage('');
            // Clear error message after 5 seconds
            setTimeout(() => setErrorMessage(''), 5000);
        }
    };

    return (
        <div>
            <h2>Membership Registration Form</h2>
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
            <form onSubmit={handleSubmit}>
                {/* Form fields */}
                {Object.entries(formData).map(([key, value]) => (
                    key !== 'passportPhoto' && (
                        <div key={key}>
                            <label>{key.replace(/([A-Z])/g, ' $1').toUpperCase()}:</label>
                            <input
                                type={key === 'dob' ? 'date' : key === 'childrenCount' ? 'number' : 'text'}
                                name={key}
                                value={value}
                                onChange={handleChange}
                                required={key !== 'otherName' && key !== 'partnerName' && key !== 'childrenNames'}
                            />
                        </div>
                    )
                ))}
                <div>
                    <label>
                        <input
                            type="checkbox"
                            name="agreement"
                            checked={formData.agreement}
                            onChange={handleCheckboxChange}
                        />
                        I hereby confirm that the above information is true to the best of my knowledge and I agree to abide by the rules and regulations of the Edo Cultural Association.
                    </label>
                </div>
                <div>
                    <label>UPLOAD PASSPORT PHOTO:</label>
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleFileChange}
                    />
                </div>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default MembershipRegister;
