import React from 'react';
import { Container, Table, Badge, Button } from 'react-bootstrap';
import { useShared } from '../../context/SharedContext';

const PartnershipManagement = () => {
  const { partnershipRequests, updatePartnershipStatus, deletePartnership } = useShared();

  const handleStatusChange = (id, newStatus) => {
    updatePartnershipStatus(id, newStatus);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this partnership request? This action cannot be undone.')) {
      deletePartnership(id);
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      pending: 'warning',
      approved: 'success',
      rejected: 'danger'
    };
    return <Badge bg={variants[status]}>{status.toUpperCase()}</Badge>;
  };

  return (
    <Container className="py-5">
      <h2 className="mb-4">Partnership Requests</h2>
      
      {partnershipRequests.length === 0 ? (
        <p className="text-center text-muted">No partnership requests yet.</p>
      ) : (
        <Table responsive striped hover>
          <thead>
            <tr>
              <th>Date</th>
              <th>Organization</th>
              <th>Contact Person</th>
              <th>Email</th>
              <th>Type</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {partnershipRequests.map((request) => (
              <tr key={request.id}>
                <td>{new Date(request.submittedAt).toLocaleDateString()}</td>
                <td>{request.organizationName}</td>
                <td>{request.contactPerson}</td>
                <td>
                  <a href={`mailto:${request.email}`}>{request.email}</a>
                </td>
                <td>{request.partnershipType}</td>
                <td>{getStatusBadge(request.status)}</td>
                <td>
                  <div className="d-flex gap-2">
                    {request.status === 'pending' && (
                      <>
                        <Button
                          variant="success"
                          size="sm"
                          onClick={() => handleStatusChange(request.id, 'approved')}
                        >
                          Approve
                        </Button>
                        <Button
                          variant="danger"
                          size="sm"
                          onClick={() => handleStatusChange(request.id, 'rejected')}
                        >
                          Reject
                        </Button>
                      </>
                    )}
                    <Button
                      variant="info"
                      size="sm"
                      onClick={() => window.alert(request.description)}
                    >
                      View Details
                    </Button>
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={() => handleDelete(request.id)}
                    >
                      Delete
                    </Button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </Container>
  );
};

export default PartnershipManagement;
