import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('members');
  const [members, setMembers] = useState([]);
  const [events, setEvents] = useState([]);
  const [gallery, setGallery] = useState([]);
  const [news, setNews] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Check authentication
    const isAuthenticated = localStorage.getItem('adminAuthenticated');
    if (!isAuthenticated) {
      navigate('/admin');
    }
    // Load data (in a real application, this would fetch from an API)
    loadData();
  }, [navigate]);

  const loadData = () => {
    // In a real application, these would be API calls
    // For now, we'll use mock data
    setMembers(JSON.parse(localStorage.getItem('members') || '[]'));
    setEvents(JSON.parse(localStorage.getItem('events') || '[]'));
    setGallery(JSON.parse(localStorage.getItem('gallery') || '[]'));
    setNews(JSON.parse(localStorage.getItem('news') || '[]'));
  };

  const handleLogout = () => {
    localStorage.removeItem('adminAuthenticated');
    navigate('/admin');
  };

  const [newEvent, setNewEvent] = useState({
    title: '',
    date: '',
    location: '',
    description: ''
  });

  const [newNews, setNewNews] = useState({
    title: '',
    content: '',
    date: new Date().toISOString().split('T')[0]
  });

  const handleAddEvent = (e) => {
    e.preventDefault();
    const events = JSON.parse(localStorage.getItem('events') || '[]');
    events.push({ ...newEvent, id: Date.now() });
    localStorage.setItem('events', JSON.stringify(events));
    setEvents(events);
    setNewEvent({ title: '', date: '', location: '', description: '' });
  };

  const handleAddNews = (e) => {
    e.preventDefault();
    const news = JSON.parse(localStorage.getItem('news') || '[]');
    news.push({ ...newNews, id: Date.now() });
    localStorage.setItem('news', JSON.stringify(news));
    setNews(news);
    setNewNews({ title: '', content: '', date: new Date().toISOString().split('T')[0] });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const gallery = JSON.parse(localStorage.getItem('gallery') || '[]');
        gallery.push({
          id: Date.now(),
          url: reader.result,
          caption: file.name
        });
        localStorage.setItem('gallery', JSON.stringify(gallery));
        setGallery(gallery);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="admin-dashboard">
      <header className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </header>

      <nav className="dashboard-nav">
        <button
          className={`nav-btn ${activeTab === 'members' ? 'active' : ''}`}
          onClick={() => setActiveTab('members')}
        >
          Members
        </button>
        <button
          className={`nav-btn ${activeTab === 'events' ? 'active' : ''}`}
          onClick={() => setActiveTab('events')}
        >
          Events
        </button>
        <button
          className={`nav-btn ${activeTab === 'gallery' ? 'active' : ''}`}
          onClick={() => setActiveTab('gallery')}
        >
          Gallery
        </button>
        <button
          className={`nav-btn ${activeTab === 'news' ? 'active' : ''}`}
          onClick={() => setActiveTab('news')}
        >
          News
        </button>
      </nav>

      <main className="dashboard-content">
        {activeTab === 'members' && (
          <div className="members-section">
            <h2>Registered Members</h2>
            <div className="members-list">
              {members.map(member => (
                <div key={member.id} className="member-card">
                  <h3>{member.surname}, {member.name}</h3>
                  <p>Phone: {member.phone}</p>
                  <p>Email: {member.email}</p>
                  <button className="view-btn">View Details</button>
                </div>
              ))}
              {members.length === 0 && (
                <p className="no-data">No members registered yet.</p>
              )}
            </div>
          </div>
        )}

        {activeTab === 'events' && (
          <div className="events-section">
            <h2>Events Management</h2>
            <form onSubmit={handleAddEvent} className="add-form">
              <div className="form-group">
                <label htmlFor="eventTitle">Event Title</label>
                <input
                  type="text"
                  id="eventTitle"
                  value={newEvent.title}
                  onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="eventDate">Date</label>
                <input
                  type="date"
                  id="eventDate"
                  value={newEvent.date}
                  onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="eventLocation">Location</label>
                <input
                  type="text"
                  id="eventLocation"
                  value={newEvent.location}
                  onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="eventDescription">Description</label>
                <textarea
                  id="eventDescription"
                  value={newEvent.description}
                  onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
                  required
                />
              </div>
              <button type="submit" className="add-btn">Add Event</button>
            </form>

            <div className="events-list">
              {events.map(event => (
                <div key={event.id} className="event-card">
                  <h3>{event.title}</h3>
                  <p>Date: {new Date(event.date).toLocaleDateString()}</p>
                  <p>Location: {event.location}</p>
                  <p>{event.description}</p>
                  <button className="delete-btn">Delete</button>
                </div>
              ))}
              {events.length === 0 && (
                <p className="no-data">No events added yet.</p>
              )}
            </div>
          </div>
        )}

        {activeTab === 'gallery' && (
          <div className="gallery-section">
            <h2>Gallery Management</h2>
            <div className="upload-section">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="file-input"
              />
            </div>
            <div className="gallery-grid">
              {gallery.map(image => (
                <div key={image.id} className="gallery-item">
                  <img src={image.url} alt={image.caption} />
                  <button className="delete-btn">Delete</button>
                </div>
              ))}
              {gallery.length === 0 && (
                <p className="no-data">No images in gallery.</p>
              )}
            </div>
          </div>
        )}

        {activeTab === 'news' && (
          <div className="news-section">
            <h2>News Management</h2>
            <form onSubmit={handleAddNews} className="add-form">
              <div className="form-group">
                <label htmlFor="newsTitle">Title</label>
                <input
                  type="text"
                  id="newsTitle"
                  value={newNews.title}
                  onChange={(e) => setNewNews({ ...newNews, title: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="newsContent">Content</label>
                <textarea
                  id="newsContent"
                  value={newNews.content}
                  onChange={(e) => setNewNews({ ...newNews, content: e.target.value })}
                  required
                />
              </div>
              <button type="submit" className="add-btn">Add News</button>
            </form>

            <div className="news-list">
              {news.map(item => (
                <div key={item.id} className="news-card">
                  <h3>{item.title}</h3>
                  <p>{item.content}</p>
                  <p className="date">{new Date(item.date).toLocaleDateString()}</p>
                  <button className="delete-btn">Delete</button>
                </div>
              ))}
              {news.length === 0 && (
                <p className="no-data">No news articles added yet.</p>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default AdminDashboard;
