import React, { useState } from 'react';

const AdminLogin = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = (event) => {
        event.preventDefault();
        
        // Replace this with your actual login logic
        if (username === 'admin' && password === 'password') {
            setIsLoggedIn(true);
            alert('Login successful!');
            // Here, you might redirect the admin to the admin dashboard or another page
        } else {
            alert('Invalid username or password');
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setPassword('');
        alert('Logged out successfully!');
    };

    return (
        <div>
            <h2>Admin Login</h2>
            {isLoggedIn ? (
                <div>
                    <h3>Welcome, Admin!</h3>
                    <button onClick={handleLogout}>Logout</button>
                    {/* Add links to access various admin features, like updating events */}
                    <p>
                        <a href="/events">Update Upcoming Events</a>
                    </p>
                    <p>
                        <a href="/other-admin-features">Access Other Admin Features</a>
                    </p>
                </div>
            ) : (
                <form onSubmit={handleLogin}>
                    <div>
                        <label htmlFor="username">Username:</label>
                        <input 
                            type="text" 
                            id="username" 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)} 
                            required 
                        />
                    </div>
                    <div>
                        <label htmlFor="password">Password:</label>
                        <input 
                            type="password" 
                            id="password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>
            )}
        </div>
    );
};

export default AdminLogin;
