import React, { useState, useEffect } from 'react';

const AdminLogin = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [eventDetails, setEventDetails] = useState({ title: '', date: '', description: '' });
    const [photo, setPhoto] = useState(null);
    const [photoPreview, setPhotoPreview] = useState(null);
    const [video, setVideo] = useState(null);
    const [videoPreview, setVideoPreview] = useState(null);
    const [failedAttempts, setFailedAttempts] = useState(0);
    const [isResetPasswordVisible, setIsResetPasswordVisible] = useState(false);
    const [newPassword, setNewPassword] = useState('');
    const [members, setMembers] = useState([]);

    useEffect(() => {
        if (isLoggedIn) {
            // Fetch membership data from backend (simulated with static data here)
            fetch('https://your-backend-url.com/members') // Replace with actual backend endpoint
                .then((response) => response.json())
                .then((data) => setMembers(data))
                .catch((error) => console.error('Error fetching members:', error));
        }
    }, [isLoggedIn]);

    const handleLogin = (event) => {
        event.preventDefault();
        if (username === 'admin' && password === 'password') {
            setIsLoggedIn(true);
            setFailedAttempts(0);
            alert('Login successful!');
        } else {
            setFailedAttempts((prev) => prev + 1);
            alert('Invalid username or password');
            if (failedAttempts >= 2) {
                setIsResetPasswordVisible(true);
            }
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setPassword('');
        setEventDetails({ title: '', date: '', description: '' });
        setPhoto(null);
        setPhotoPreview(null);
        setVideo(null);
        setVideoPreview(null);
        alert('Logged out successfully!');
    };

    const handleEventChange = (e) => {
        const { name, value } = e.target;
        setEventDetails((prevDetails) => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handleFileChange = (e, type) => {
        const file = e.target.files[0];
        if (type === 'photo') {
            setPhoto(file);
            setPhotoPreview(URL.createObjectURL(file));
        } else if (type === 'video') {
            setVideo(file);
            setVideoPreview(URL.createObjectURL(file));
        }
    };

    const handleEventSubmit = (e) => {
        e.preventDefault();
        
        if (!photo || !video) {
            alert('Please select both a photo and a video to upload.');
            return;
        }

        const formData = new FormData();
        formData.append('title', eventDetails.title);
        formData.append('date', eventDetails.date);
        formData.append('description', eventDetails.description);
        formData.append('photo', photo);
        formData.append('video', video);

        // Replace with actual backend URL for uploading event data and media
        fetch('https://your-backend-url.com/upload', {
            method: 'POST',
            body: formData,
        })
        .then((response) => response.json())
        .then((data) => {
            console.log('Event added successfully:', data);
            alert('Event added successfully!');
            setEventDetails({ title: '', date: '', description: '' });
            setPhoto(null);
            setPhotoPreview(null);
            setVideo(null);
            setVideoPreview(null);
        })
        .catch((error) => {
            console.error('Error adding event:', error);
            alert('There was an error adding the event.');
        });
    };

    const handlePasswordReset = (e) => {
        e.preventDefault();
        console.log('New Password:', newPassword);
        alert('Password has been reset successfully!');
        setIsResetPasswordVisible(false);
        setNewPassword('');
        setIsLoggedIn(true);
        setUsername('admin');
        setPassword(newPassword);
    };

    return (
        <div>
            <h2>Admin Login</h2>
            {isLoggedIn ? (
                <div>
                    <h3>Welcome, Admin!</h3>
                    <button onClick={handleLogout}>Logout</button>

                    {/* Event management form */}
                    <form onSubmit={handleEventSubmit}>
                        <h4>Add Event</h4>
                        <div>
                            <label htmlFor="title">Event Title:</label>
                            <input 
                                type="text" 
                                id="title" 
                                name="title" 
                                value={eventDetails.title} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="date">Event Date:</label>
                            <input 
                                type="date" 
                                id="date" 
                                name="date" 
                                value={eventDetails.date} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="description">Description:</label>
                            <textarea 
                                id="description" 
                                name="description" 
                                value={eventDetails.description} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="photo">Event Photo:</label>
                            <input 
                                type="file" 
                                id="photo" 
                                name="photo" 
                                accept="image/*"
                                onChange={(e) => handleFileChange(e, 'photo')} 
                                required 
                            />
                            {photoPreview && (
                                <div>
                                    <img src={photoPreview} alt="Preview" width="200" />
                                </div>
                            )}
                        </div>
                        <div>
                            <label htmlFor="video">Event Video:</label>
                            <input 
                                type="file" 
                                id="video" 
                                name="video" 
                                accept="video/*"
                                onChange={(e) => handleFileChange(e, 'video')} 
                                required 
                            />
                            {videoPreview && (
                                <div>
                                    <video width="200" controls>
                                        <source src={videoPreview} type="video/mp4" />
                                        Your browser does not support the video tag.
                                    </video>
                                </div>
                            )}
                        </div>
                        <button type="submit">Add Event</button>
                    </form>

                    {/* Membership Register */}
                    <div>
                        <h4>Membership Register</h4>
                        <ul>
                            {members.map((member, index) => (
                                <li key={index}>{member.name} - {member.email}</li>
                            ))}
                        </ul>
                    </div>
                </div>
            ) : (
                <form onSubmit={handleLogin}>
                    <div>
                        <label htmlFor="username">Username:</label>
                        <input 
                            type="text" 
                            id="username" 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)} 
                            required 
                        />
                    </div>
                    <div>
                        <label htmlFor="password">Password:</label>
                        <input 
                            type="password" 
                            id="password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>
            )}
            {isResetPasswordVisible && (
                <div>
                    <h4>Reset Password</h4>
                    <form onSubmit={handlePasswordReset}>
                        <div>
                            <label htmlFor="newPassword">New Password:</label>
                            <input 
                                type="password" 
                                id="newPassword" 
                                value={newPassword} 
                                onChange={(e) => setNewPassword(e.target.value)} 
                                required 
                            />
                        </div>
                        <button type="submit">Reset Password</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default AdminLogin;
