import React, { useState } from 'react';
import './Membership.css';

const Membership = () => {
  const [formData, setFormData] = useState({
    name: '',
    surname: '',
    email: '',
    phone: '',
    dateOfBirth: '',
    nationality: '',
    stateOfOrigin: '',
    address: '',
    occupation: '',
    identification: '',
    nextOfKin: {
      name: '',
      relationship: '',
      phone: '',
      address: ''
    },
    documents: []
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    const newDocuments = [];

    files.forEach(file => {
      const reader = new FileReader();
      reader.onloadend = () => {
        newDocuments.push(reader.result);
        setFormData(prev => ({
          ...prev,
          documents: newDocuments
        }));
      };
      reader.readAsDataURL(file);
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Get existing applications or initialize empty array
    const existingApplications = JSON.parse(localStorage.getItem('membershipApplications') || '[]');

    // Add new application with submission date and status
    const newApplication = {
      ...formData,
      id: Date.now(),
      submissionDate: new Date().toISOString(),
      status: 'pending'
    };

    // Save to localStorage
    localStorage.setItem('membershipApplications', JSON.stringify([...existingApplications, newApplication]));

    // Reset form and show success message
    setFormData({
      name: '',
      surname: '',
      email: '',
      phone: '',
      dateOfBirth: '',
      nationality: '',
      stateOfOrigin: '',
      address: '',
      occupation: '',
      identification: '',
      nextOfKin: {
        name: '',
        relationship: '',
        phone: '',
        address: ''
      },
      documents: []
    });
    setSubmitted(true);
  };

  return (
    <div className="membership-container">
      <h1>Membership Registration</h1>
      {submitted ? (
        <div className="success-message">
          <h2>Thank you for your application!</h2>
          <p>Your membership application has been submitted successfully. We will review it and get back to you soon.</p>
          <button onClick={() => setSubmitted(false)}>Submit Another Application</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="membership-form">
          <div className="form-section">
            <h2>Personal Information</h2>
            <div className="form-group">
              <label htmlFor="name">First Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="surname">Last Name</label>
              <input
                type="text"
                id="surname"
                name="surname"
                value={formData.surname}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="phone">Phone Number</label>
              <input
                type="tel"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="dateOfBirth">Date of Birth</label>
              <input
                type="date"
                id="dateOfBirth"
                name="dateOfBirth"
                value={formData.dateOfBirth}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="nationality">Nationality</label>
              <input
                type="text"
                id="nationality"
                name="nationality"
                value={formData.nationality}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="stateOfOrigin">State of Origin</label>
              <input
                type="text"
                id="stateOfOrigin"
                name="stateOfOrigin"
                value={formData.stateOfOrigin}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="form-section">
            <h2>Contact Information</h2>
            <div className="form-group">
              <label htmlFor="address">Address</label>
              <textarea
                id="address"
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="occupation">Occupation</label>
              <input
                type="text"
                id="occupation"
                name="occupation"
                value={formData.occupation}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="identification">ID Number/Passport</label>
              <input
                type="text"
                id="identification"
                name="identification"
                value={formData.identification}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="form-section">
            <h2>Next of Kin Information</h2>
            <div className="form-group">
              <label htmlFor="nextOfKin.name">Name</label>
              <input
                type="text"
                id="nextOfKin.name"
                name="nextOfKin.name"
                value={formData.nextOfKin.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="nextOfKin.relationship">Relationship</label>
              <input
                type="text"
                id="nextOfKin.relationship"
                name="nextOfKin.relationship"
                value={formData.nextOfKin.relationship}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="nextOfKin.phone">Phone Number</label>
              <input
                type="tel"
                id="nextOfKin.phone"
                name="nextOfKin.phone"
                value={formData.nextOfKin.phone}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="nextOfKin.address">Address</label>
              <textarea
                id="nextOfKin.address"
                name="nextOfKin.address"
                value={formData.nextOfKin.address}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="form-section">
            <h2>Documents</h2>
            <div className="form-group">
              <label htmlFor="documents">Upload Documents (ID, Passport, etc.)</label>
              <input
                type="file"
                id="documents"
                multiple
                accept="image/*,.pdf"
                onChange={handleFileChange}
                required
              />
              {formData.documents && formData.documents.length > 0 && (
                <div className="preview-documents">
                  {formData.documents.map((doc, index) => (
                    <div key={index} className="document-preview">
                      {doc.startsWith('data:image') ? (
                        <img src={doc} alt={`Document ${index + 1}`} />
                      ) : (
                        <div className="pdf-preview">PDF Document {index + 1}</div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          <button type="submit" className="submit-btn">Submit Application</button>
        </form>
      )}
    </div>
  );
};

export default Membership;
