import React, { useState } from 'react';

const AdminLogin = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [eventDetails, setEventDetails] = useState({ title: '', date: '', description: '' });
    const [photo, setPhoto] = useState(null);
    const [photoPreview, setPhotoPreview] = useState(null);
    const [failedAttempts, setFailedAttempts] = useState(0);
    const [isResetPasswordVisible, setIsResetPasswordVisible] = useState(false);
    const [newPassword, setNewPassword] = useState('');

    const handleLogin = (event) => {
        event.preventDefault();
        if (username === 'admin' && password === 'admin') {
            setIsLoggedIn(true);
            setFailedAttempts(0);
            alert('Login successful!');
        } else {
            setFailedAttempts((prev) => prev + 1);
            alert('Invalid username or password');
            if (failedAttempts >= 2) {
                setIsResetPasswordVisible(true);
            }
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setPassword('');
        setEventDetails({ title: '', date: '', description: '' });
        setPhoto(null);
        setPhotoPreview(null);
        alert('Logged out successfully!');
    };

    const handleEventChange = (e) => {
        const { name, value } = e.target;
        setEventDetails((prevDetails) => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setPhoto(file);
        setPhotoPreview(URL.createObjectURL(file));
    };

    const handleEventSubmit = (e) => {
        e.preventDefault();
        
        if (!photo) {
            alert('Please select an image to upload.');
            return;
        }

        const formData = new FormData();
        formData.append('title', eventDetails.title);
        formData.append('date', eventDetails.date);
        formData.append('description', eventDetails.description);
        formData.append('photo', photo);

        // Replace with actual backend URL for uploading event data and image
        fetch('https://your-backend-url.com/upload', {
            method: 'POST',
            body: formData,
        })
        .then((response) => response.json())
        .then((data) => {
            console.log('Event added successfully:', data);
            alert('Event added successfully!');
            setEventDetails({ title: '', date: '', description: '' });
            setPhoto(null);
            setPhotoPreview(null);
        })
        .catch((error) => {
            console.error('Error adding event:', error);
            alert('There was an error adding the event.');
        });
    };

    const handlePasswordReset = (e) => {
        e.preventDefault();
        console.log('New Password:', newPassword);
        alert('Password has been reset successfully!');
        setIsResetPasswordVisible(false);
        setNewPassword('');
        setIsLoggedIn(true);
        setUsername('admin');
        setPassword(newPassword);
    };

    return (
        <div>
            <h2>Admin Login</h2>
            {isLoggedIn ? (
                <div>
                    <h3>Welcome, Admin!</h3>
                    <button onClick={handleLogout}>Logout</button>

                    {/* Event management form */}
                    <form onSubmit={handleEventSubmit}>
                        <h4>Add Event</h4>
                        <div>
                            <label htmlFor="title">Event Title:</label>
                            <input 
                                type="text" 
                                id="title" 
                                name="title" 
                                value={eventDetails.title} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="date">Event Date:</label>
                            <input 
                                type="date" 
                                id="date" 
                                name="date" 
                                value={eventDetails.date} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="description">Description:</label>
                            <textarea 
                                id="description" 
                                name="description" 
                                value={eventDetails.description} 
                                onChange={handleEventChange} 
                                required 
                            />
                        </div>
                        <div>
                            <label htmlFor="photo">Event Photo:</label>
                            <input 
                                type="file" 
                                id="photo" 
                                name="photo" 
                                accept="image/*"
                                onChange={handleFileChange} 
                                required 
                            />
                            {photoPreview && (
                                <div>
                                    <img src={photoPreview} alt="Preview" width="200" />
                                </div>
                            )}
                        </div>
                        <button type="submit">Add Event</button>
                    </form>
                </div>
            ) : (
                <form onSubmit={handleLogin}>
                    <div>
                        <label htmlFor="username">Username:</label>
                        <input 
                            type="text" 
                            id="username" 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)} 
                            required 
                        />
                    </div>
                    <div>
                        <label htmlFor="password">Password:</label>
                        <input 
                            type="password" 
                            id="password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>
            )}
            {isResetPasswordVisible && (
                <div>
                    <h4>Reset Password</h4>
                    <form onSubmit={handlePasswordReset}>
                        <div>
                            <label htmlFor="newPassword">New Password:</label>
                            <input 
                                type="password" 
                                id="newPassword" 
                                value={newPassword} 
                                onChange={(e) => setNewPassword(e.target.value)} 
                                required 
                            />
                        </div>
                        <button type="submit">Reset Password</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default AdminLogin;
