import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import MembershipRegister from './membershipRegister';
import Events from './events';
import PhotosVideo from './photosVideo';
import Donation from './donation';
import About from './about';
import Contact from './contact';
import AdminLogin from './adminlogin';
import './styles.css'; // Import your CSS styles

// Header component
function Header() {
    return (
        <header>
            {/* Nigeria flag on the top left */}
            <img src="" alt="Nigeria Flag" className="flag flag-left" />

            {/* Italy flag on the top right */}
            <img 
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQkU0tjUdDG83Yt6Dxrya21YMN3L00LWB2Vew&s" 
                alt="Italy Flag" 
                className="flag flag-right" 
            />
            
            {/* Centered logo */}
            <a href="/">
                <img 
                    src="C:/Users/king/Downloads/pic (1).jpg" // Update this path if necessary
                    alt="Edo Cultural Association Logo" 
                    className="logo" 
                />
            </a>
            
            <h1>Edo Cultural Association</h1>
            <p>Friuli Venezia Giulia Udine Italy</p>
        </header>
    );
}

// Navigation component
function Nav() {
    return (
        <nav>
            <Link to="/">Homepage</Link>
            <Link to="/membership-register">Membership Register</Link>
            <Link to="/events">Events</Link>
            <Link to="/photos-video">Photos & Video</Link>
            <Link to="/donation">Donation</Link>
            <Link to="/about">About</Link>
            <Link to="/contact">Contact</Link>
            <Link to="/admin">Admin</Link>
        </nav>
    );
}

// Main component
function Main() {
    return (
        <main>
            <Routes>
                <Route path="/" element={<Homepage />} />
                <Route path="/membership-register" element={<MembershipRegister />} />
                <Route path="/events" element={<Events />} />
                <Route path="/photos-video" element={<PhotosVideo />} />
                <Route path="/donation" element={<Donation />} />
                <Route path="/about" element={<About />} />
                <Route path="/contact" element={<Contact />} />
                <Route path="/admin" element={<AdminLogin />} />
            </Routes>
        </main>
    );
}

// Dummy component for the homepage
function Homepage() {
    return (
        <section id="homepage">
            <h2>Welcome to Edo Cultural Association</h2>
            <div className="gallery" id="gallery">
                <img src="" alt="Cultural Event 1" />
                <img 
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTHI8CJ42KM2mY-LdozWviagZpxQ5BFJ68wK2JXjKck0Ah7l3q5G3lz6yHv4v1yjA1S99w&usqp=CAU" 
                    alt="Cultural Event 2" 
                />
                <img 
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS_Eo9t5DrqDLWTc85y77vtrLcfNLWD38Zmag&s" 
                    alt="Cultural Event 3" 
                />
                <img 
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSiQ9Rg6-oTnU49ALoLvkxFkZVRUeY8kzvwFQ&s" 
                    alt="Cultural Event 4" 
                />
                <img 
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTOsPnmoOLhaady5ouPuVTepFeSJVlhVh5UFw&s" 
                    alt="Cultural Event 5" 
                />
                <img 
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQId9DJJIvnX7bLhIUL8bbcRhSZCLK0xAEumw&s" 
                    alt="Cultural Event 6" 
                />
                {/* Add more images as needed */}
            </div>
        </section>
    );
}

// Footer component
function Footer() {
    return (
        <footer>
            <div className="marquee">
                &copy; {new Date().getFullYear()} Edo Cultural Association. All rights reserved.
            </div>
        </footer>
    );
}

function App() {
    return (
        <Router>
            <div id="app"> {/* Add an ID for styling */}
                <Header />
                <Nav />
                <Main />
                <Footer />
            </div>
        </Router>
    );
}

export default App;
