import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')) || null);
    const [error, setError] = useState(null);
    const [loginSuccess, setLoginSuccess] = useState(false); // New: Track login success
    const [events, setEvents] = useState(JSON.parse(localStorage.getItem('events')) || []);

    const login = (username, password) => {
        return new Promise((resolve, reject) => {
            if (username === 'admin' && password === 'adminPassword') {
                const userData = { name: 'Admin', role: 'admin' };
                setUser(userData);
                localStorage.setItem('user', JSON.stringify(userData));
                setError(null);
                setLoginSuccess(true); // Show success message
                resolve();
            } else {
                setError('Invalid username or password');
                setLoginSuccess(false);
                reject(new Error('Invalid credentials'));
            }
        });
    };

    const logout = () => {
        setUser(null);
        localStorage.removeItem('user');
        setLoginSuccess(false); // Reset login success
    };

    const addEvent = (eventData) => {
        const updatedEvents = [...events, eventData];
        setEvents(updatedEvents);
        localStorage.setItem('events', JSON.stringify(updatedEvents));
    };

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        const storedEvents = JSON.parse(localStorage.getItem('events'));
        if (storedUser) setUser(storedUser);
        if (storedEvents) setEvents(storedEvents);
    }, []);

    return (
        <AuthContext.Provider value={{ user, login, logout, error, loginSuccess, events, addEvent }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
