import React, { useState } from 'react';
import './Membership.css';

const Membership = () => {
  const [formData, setFormData] = useState({
    name: '',
    surname: '',
    email: '',
    phone: '',
    dateOfBirth: '',
    nationality: '',
    stateOfOrigin: '',
    address: '',
    occupation: '',
    identification: '',
    nextOfKin: {
      name: '',
      relationship: '',
      phone: '',
      address: ''
    },
    documents: []
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith('nextOfKin.')) {
      const kinField = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        nextOfKin: {
          ...prev.nextOfKin,
          [kinField]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setFormData(prev => ({
      ...prev,
      documents: files
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // Create a FormData object to handle file uploads
      const formDataToSubmit = new FormData();

      // Append all form fields
      Object.keys(formData).forEach(key => {
        if (key === 'nextOfKin') {
          formDataToSubmit.append(key, JSON.stringify(formData[key]));
        } else if (key === 'documents') {
          formData.documents.forEach((file, index) => {
            formDataToSubmit.append(`document${index}`, file);
          });
        } else {
          formDataToSubmit.append(key, formData[key]);
        }
      });

      // Add submission date
      formDataToSubmit.append('submissionDate', new Date().toISOString());
      formDataToSubmit.append('status', 'pending');

      // Get existing applications from localStorage
      const existingApplications = JSON.parse(localStorage.getItem('membershipApplications') || '[]');

      // Add new application
      const newApplication = {
        id: Date.now(),
        ...Object.fromEntries(formDataToSubmit),
        status: 'pending',
        submissionDate: new Date().toISOString()
      };

      existingApplications.push(newApplication);

      // Save to localStorage
      localStorage.setItem('membershipApplications', JSON.stringify(existingApplications));

      setSubmitStatus('success');
      // Reset form
      setFormData({
        name: '',
        surname: '',
        email: '',
        phone: '',
        dateOfBirth: '',
        nationality: '',
        stateOfOrigin: '',
        address: '',
        occupation: '',
        identification: '',
        nextOfKin: {
          name: '',
          relationship: '',
          phone: '',
          address: ''
        },
        documents: []
      });
    } catch (error) {
      setSubmitStatus('error');
      console.error('Error submitting application:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="membership-page">
      <div className="membership-container">
        <h1>Membership Registration</h1>
        <p className="membership-intro">
          Join the Edo Cultural Association Italy and become part of our vibrant community.
          Please fill out the form below with your details.
        </p>

        {submitStatus === 'success' && (
          <div className="success-message">
            Your application has been submitted successfully! We will review it and get back to you soon.
          </div>
        )}

        {submitStatus === 'error' && (
          <div className="error-message">
            There was an error submitting your application. Please try again.
          </div>
        )}

        <form onSubmit={handleSubmit} className="membership-form">
          <div className="form-section">
            <h2>Personal Information</h2>
            <div className="form-grid">
              <div className="form-group">
                <label htmlFor="name">First Name *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="surname">Last Name *</label>
                <input
                  type="text"
                  id="surname"
                  name="surname"
                  value={formData.surname}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email Address *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="phone">Phone Number *</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="dateOfBirth">Date of Birth *</label>
                <input
                  type="date"
                  id="dateOfBirth"
                  name="dateOfBirth"
                  value={formData.dateOfBirth}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="nationality">Nationality *</label>
                <input
                  type="text"
                  id="nationality"
                  name="nationality"
                  value={formData.nationality}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="stateOfOrigin">State of Origin *</label>
                <input
                  type="text"
                  id="stateOfOrigin"
                  name="stateOfOrigin"
                  value={formData.stateOfOrigin}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Contact Information</h2>
            <div className="form-grid">
              <div className="form-group full-width">
                <label htmlFor="address">Address *</label>
                <textarea
                  id="address"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="occupation">Occupation *</label>
                <input
                  type="text"
                  id="occupation"
                  name="occupation"
                  value={formData.occupation}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="identification">ID Number/Passport *</label>
                <input
                  type="text"
                  id="identification"
                  name="identification"
                  value={formData.identification}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Next of Kin Information</h2>
            <div className="form-grid">
              <div className="form-group">
                <label htmlFor="nextOfKin.name">Name *</label>
                <input
                  type="text"
                  id="nextOfKin.name"
                  name="nextOfKin.name"
                  value={formData.nextOfKin.name}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="nextOfKin.relationship">Relationship *</label>
                <input
                  type="text"
                  id="nextOfKin.relationship"
                  name="nextOfKin.relationship"
                  value={formData.nextOfKin.relationship}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="nextOfKin.phone">Phone Number *</label>
                <input
                  type="tel"
                  id="nextOfKin.phone"
                  name="nextOfKin.phone"
                  value={formData.nextOfKin.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="nextOfKin.address">Address *</label>
                <input
                  type="text"
                  id="nextOfKin.address"
                  name="nextOfKin.address"
                  value={formData.nextOfKin.address}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Required Documents</h2>
            <div className="form-group">
              <label htmlFor="documents">Upload Documents (ID, Passport, etc.) *</label>
              <input
                type="file"
                id="documents"
                name="documents"
                onChange={handleFileChange}
                multiple
                required
              />
              <p className="help-text">You can upload multiple documents. Supported formats: PDF, JPG, PNG</p>
            </div>
          </div>

          <div className="form-section">
            <button type="submit" className="submit-btn" disabled={isSubmitting}>
              {isSubmitting ? 'Submitting...' : 'Submit Application'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Membership;
