import React, { createContext, useContext, useState, useEffect } from 'react';

const SharedContext = createContext();

export const SharedProvider = ({ children }) => {
  const [events, setEvents] = useState(() => {
    const savedEvents = localStorage.getItem('events');
    return savedEvents ? JSON.parse(savedEvents) : [];
  });

  const [gallery, setGallery] = useState(() => {
    const savedGallery = localStorage.getItem('gallery');
    return savedGallery ? JSON.parse(savedGallery) : [];
  });

  const [partnershipRequests, setPartnershipRequests] = useState(() => {
    const savedRequests = localStorage.getItem('partnershipRequests');
    return savedRequests ? JSON.parse(savedRequests) : [];
  });

  const [meetings, setMeetings] = useState([]);
  const [partnerships, setPartnerships] = useState([]);
  const [galleries, setGalleries] = useState([]);

  // Save events to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('events', JSON.stringify(events));
  }, [events]);

  // Save gallery to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('gallery', JSON.stringify(gallery));
  }, [gallery]);

  // Save partnership requests to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('partnershipRequests', JSON.stringify(partnershipRequests));
  }, [partnershipRequests]);

  // Meetings functions
  const addMeeting = (meeting) => {
    setMeetings([...meetings, { ...meeting, id: Date.now() }]);
  };

  const updateMeeting = (id, updatedMeeting) => {
    setMeetings(meetings.map(meeting => 
      meeting.id === id ? { ...meeting, ...updatedMeeting } : meeting
    ));
  };

  const deleteMeeting = (id) => {
    setMeetings(meetings.filter(meeting => meeting.id !== id));
  };

  // Events functions
  const addEvent = (event) => {
    setEvents(prevEvents => [...prevEvents, { ...event, id: Date.now() }]);
  };

  const updateEvent = (id, updatedEvent) => {
    setEvents(prevEvents => 
      prevEvents.map(event => event.id === id ? { ...updatedEvent, id } : event)
    );
  };

  const deleteEvent = (id) => {
    setEvents(prevEvents => prevEvents.filter(event => event.id !== id));
  };

  // Partnerships functions
  const addPartnership = (partnership) => {
    setPartnerships([...partnerships, { ...partnership, id: Date.now() }]);
  };

  const updatePartnership = (id, updatedPartnership) => {
    setPartnerships(partnerships.map(partnership => 
      partnership.id === id ? { ...partnership, ...updatedPartnership } : partnership
    ));
  };

  const deletePartnership = (id) => {
    setPartnerships(partnerships.filter(partnership => partnership.id !== id));
  };

  // Gallery functions
  const addGalleryItem = (item) => {
    setGalleries([...galleries, { ...item, id: Date.now() }]);
  };

  const updateGalleryItem = (id, updatedItem) => {
    setGalleries(galleries.map(item => 
      item.id === id ? { ...item, ...updatedItem } : item
    ));
  };

  const deleteGalleryItem = (id) => {
    setGalleries(galleries.filter(item => item.id !== id));
  };

  return (
    <SharedContext.Provider value={{
      events,
      addEvent,
      updateEvent,
      deleteEvent,
      meetings,
      addMeeting,
      updateMeeting,
      deleteMeeting,
      partnerships,
      addPartnership,
      updatePartnership,
      deletePartnership,
      galleries,
      addGalleryItem,
      updateGalleryItem,
      deleteGalleryItem
    }}>
      {children}
    </SharedContext.Provider>
  );
};

export const useShared = () => {
  const context = useContext(SharedContext);
  if (!context) {
    throw new Error('useShared must be used within a SharedProvider');
  }
  return context;
};

export default SharedContext;
