import React, { useState } from 'react';

// Sample events data structure
const initialEvents = [
    { id: 1, name: "Cultural Festival", date: "2024-11-01", description: "A celebration of culture.", images: [] },
    { id: 2, name: "Traditional Dance", date: "2024-12-05", description: "A showcase of traditional dances.", images: [] },
];

function Events() {
    const [events, setEvents] = useState(initialEvents);
    const [newEvent, setNewEvent] = useState({ name: '', date: '', description: '', images: [] });
    const [imageFiles, setImageFiles] = useState([]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewEvent((prev) => ({ ...prev, [name]: value }));
    };

    const handleFileChange = (e) => {
        setImageFiles([...e.target.files]);
    };

    const handleAddEvent = () => {
        const eventWithImages = { ...newEvent, id: events.length + 1, images: Array.from(imageFiles) };
        setEvents((prev) => [...prev, eventWithImages]);
        setNewEvent({ name: '', date: '', description: '', images: [] });
        setImageFiles([]);
    };

    const handleRemoveEvent = (id) => {
        setEvents((prev) => prev.filter((event) => event.id !== id));
    };

    const handleModifyEvent = (id) => {
        const eventToModify = events.find((event) => event.id === id);
        setNewEvent(eventToModify);
        handleRemoveEvent(id); // Remove the event after setting it for modification
    };

    return (
        <div>
            <h2>Upcoming Events</h2>
            <div>
                <h3>Add New Event</h3>
                <input 
                    type="text" 
                    name="name" 
                    placeholder="Event Name" 
                    value={newEvent.name} 
                    onChange={handleInputChange} 
                />
                <input 
                    type="date" 
                    name="date" 
                    value={newEvent.date} 
                    onChange={handleInputChange} 
                />
                <textarea 
                    name="description" 
                    placeholder="Description" 
                    value={newEvent.description} 
                    onChange={handleInputChange} 
                />
                <input 
                    type="file" 
                    multiple 
                    onChange={handleFileChange} 
                />
                <button onClick={handleAddEvent}>Add Event</button>
            </div>

            <h3>Current Events</h3>
            {events.map((event) => (
                <div key={event.id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px 0' }}>
                    <h4>{event.name}</h4>
                    <p>Date: {event.date}</p>
                    <p>Description: {event.description}</p>
                    {event.images.length > 0 && (
                        <div>
                            <h5>Images:</h5>
                            {event.images.map((file, index) => (
                                <img key={index} src={URL.createObjectURL(file)} alt={`Event Image ${index}`} style={{ width: '100px', height: '100px', margin: '5px' }} />
                            ))}
                        </div>
                    )}
                    <button onClick={() => handleModifyEvent(event.id)}>Modify</button>
                    <button onClick={() => handleRemoveEvent(event.id)}>Remove</button>
                </div>
            ))}
        </div>
    );
}

export default Events;
